// App.tsx
import React, { useState, useEffect, lazy, Suspense } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Sidebar from './components/Sidebar/Sidebar';
import { ThemeProvider } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import { lightTheme, darkTheme } from './theme';
import './App.scss';
import ProtectedRoute from './components/ProtectedRoute';
import axios from 'axios';
import { Box, CircularProgress, useMediaQuery } from '@mui/material';
// React Query imports
import { QueryClient, QueryClientProvider } from '@tanstack/react-query';
import { ReactQueryDevtools } from '@tanstack/react-query-devtools';
// Import ToastContainer for centralized toast notifications
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
// Import the necessary hooks
import { useMediaTypesWithUsageCounts, useUserProfile } from './hooks/query-hooks';
import type { User } from './hooks/query-hooks';
// Remove Redux imports if no longer used for user state here
// import { useDispatch, useSelector } from 'react-redux';
// import { RootState, AppDispatch } from './store/store';
// import MediaApprovalPage from './pages/MediaApprovalPage'; // Adjust path as needed

// Create React Query client with improved configuration
const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      refetchOnWindowFocus: false, // Disable refetching when window regains focus
      staleTime: 1000 * 60 * 5, // Data is considered fresh for 5 minutes
      retry: 1, // Only retry failed requests once
      // Adding a custom retry function to avoid retrying on 404 errors
      retryDelay: attemptIndex => Math.min(1000 * 2 ** attemptIndex, 30000), // Exponential backoff
    },
    mutations: {
      // Configure mutation defaults
      retry: 1,
      retryDelay: 1000,
    },
  },
});

// Lazy load components
const MediaDetail = lazy(() => import('./components/MediaDetail/MediaDetail'));
const Account = lazy(() => import('./pages/Account'));
const Home = lazy(() => import('./components/Home'));
const AuthPage = lazy(() => import('./pages/AuthPage'));
const PasswordSetupPage = lazy(() => import('./pages/PasswordSetup'));
const MediaLibraryPage = lazy(() => import('./pages/MediaLibraryPage'));
const AccountUsers = lazy(() => import('./pages/AccountUsers'));
const AccountTags = lazy(() => import('./pages/AccountTags'));
const AccountMediaTypes = lazy(() => import('./pages/AccountMediaTypes'));
const AccountAdminDashboard = lazy(() => import('./pages/AccountAdminDashboard'));
const StyleGuidePage = lazy(() => import('./pages/StyleGuidePage'));
const AcceptInvitation = lazy(() => import('./pages/AcceptInvitation'));
const UserPage = lazy(() => import('./pages/User'));

// Loading fallback component
const LoadingFallback = () => (
  <Box display="flex" justifyContent="center" alignItems="center" height="100vh">
    <CircularProgress />
  </Box>
);

// Create a context for theme toggling
export const ThemeContext = React.createContext({
  isDarkMode: true,
  toggleTheme: (_newTheme: 'light' | 'dark') => {}
});

// Create an axios interceptor to handle auth errors globally
axios.interceptors.response.use(
  (response) => response,
  (error) => {
    // Check if the error is a 401 Unauthorized
    if (error.response && error.response.status === 401) {
      console.log('[AXIOS INTERCEPTOR] 401 Unauthorized detected. Current path:', window.location.pathname);
      console.log('[AXIOS INTERCEPTOR] Removing authToken and refreshToken from localStorage.');
      // Clear all auth data
      localStorage.removeItem('authToken');
      localStorage.removeItem('refreshToken');
      // Only redirect if we're not already on the login page
      if (!window.location.pathname.includes('/login')) {
        console.log('[AXIOS INTERCEPTOR] Redirecting to login page.');
        window.location.href = '/login';
      }
    }
    return Promise.reject(error);
  }
);

// Define an internal component to use React Query hooks
// This is needed because the main App component provides the QueryClientProvider
const AppContent: React.FC = () => {
  // Check localStorage for saved theme preference
  const savedTheme = localStorage.getItem('theme');
  const [isDarkMode, setIsDarkMode] = useState(savedTheme ? savedTheme === 'dark' : true);
  // const dispatch = useDispatch<AppDispatch>(); // Removed, not used for user init
  // const isLoadingRedux = useSelector((state: RootState) => state.user.currentUser.isLoading); // Removed
  // const userRoleRedux = useSelector((state: RootState) => state.user.currentUser.role); // Removed
  // const userStateRedux = useSelector((state: RootState) => state.user.currentUser); // Removed
  const [isInitialized, setIsInitialized] = useState(false);
  // Check for mobile view to determine toast position
  const isMobile = useMediaQuery('(max-width:600px)');

  // --- User Profile with TanStack Query ---
  const { 
    data: userProfile, 
    isLoading: isUserLoading, 
    error: userError,
    isSuccess: isUserSuccess,
    isError: isUserFetchError
  } = useUserProfile();

  // Prefetch media types data using TanStack Query
  // This replaces the Redux mediaTypes initialization
  useMediaTypesWithUsageCounts(userProfile);

  useEffect(() => {
    // This effect block was primarily for the old custom callbacks.
    // The core logic of clearing userProfile on auth error is now in useUserProfile's internal onError.
    // The logic for localStorage.removeItem('authToken') on 401 is handled by the axios interceptor.
    // If additional side-effects are needed in App.tsx based on userProfile changes, 
    // a new useEffect watching userProfile, isUserFetchError, and userError can be added.
    if (isUserFetchError) {
      // Example: if a specific action is needed in App.tsx when user fetch fails, beyond what useUserProfile does.
      if (process.env.NODE_ENV === 'development') {
        console.error('App.tsx: User profile fetch resulted in an error state.', userError);
      }
      // If the error indicates an auth failure and the token is somehow still present, clear it.
      // This is a defensive check, as the interceptor and useUserProfile's onError should handle most cases.
      const token = localStorage.getItem('authToken');
      if (token && userError && typeof userError === 'object' && 'message' in userError) {
        const errorMessage = (userError as any).message || '';
        let isAuthFailure = errorMessage.includes('Authentication token missing');
        // Check for shape of Axios error without relying on isAxiosError
        if (!isAuthFailure && userError && typeof userError === 'object' && 'response' in userError && (userError as any).response) {
            const status = (userError as any).response.status;
            if (status === 401 || status === 403) {
                isAuthFailure = true;
            }
        }
        if (isAuthFailure) {
            localStorage.removeItem('authToken');
            // Optionally, redirect here if not handled by other mechanisms
            // window.location.href = '/login'; 
        }
      }
    }
  }, [isUserFetchError, userError]);

  useEffect(() => {
    document.documentElement.setAttribute('data-theme', isDarkMode ? 'dark' : 'light');
    // Save preference to localStorage
    localStorage.setItem('theme', isDarkMode ? 'dark' : 'light');
  }, [isDarkMode]);

  // useEffect to manage overall app initialization status
  // This effect now primarily waits for user and media types to settle.
  useEffect(() => {
    if (isUserSuccess || isUserFetchError) { // Considered initialized once user fetch attempt is complete
      setIsInitialized(true);
      if (process.env.NODE_ENV === 'development') {
        console.log('App initialization status: User fetch attempt complete.');
        if (isUserSuccess) console.log('User profile:', userProfile);
        if (isUserFetchError) console.log('User fetch error:', userError);
      }
    }
  }, [isUserSuccess, isUserFetchError, userProfile, userError]);

  // Updated to handle direct theme changes
  const toggleTheme = (newTheme: 'light' | 'dark') => {
    setIsDarkMode(newTheme === 'dark');
  };

  // Show loading fallback if user profile is loading or app is not yet initialized.
  // Ensure mediaTypes loading is also considered if it's critical before rendering.
  if (isUserLoading || !isInitialized) { 
    // Only show loading screen for protected routes, not for login/auth page
    // Check if userProfile is null (meaning not fetched or error) when deciding to show loading for protected routes.
    const currentPath = window.location.pathname;
    const isAuthPath = currentPath === '/login' || currentPath.includes('/auth');

    // If on an auth path, don't show global loading, let the auth page handle its own state.
    // If not on an auth path AND user is loading OR app not initialized, show fallback.
    if (!isAuthPath && (isUserLoading || !isInitialized)) {
      return <LoadingFallback />;
    }
  }

  return (
    <ThemeProvider theme={isDarkMode ? darkTheme : lightTheme}>
      <CssBaseline />
      <ThemeContext.Provider value={{ isDarkMode, toggleTheme }}>
        <Router>
          <div id="app-container" style={{ display: 'flex', flexDirection: 'row', width: '100vw', height: '100vh', overflow: 'hidden' }}>
            {/* Sidebar is shown if user profile fetch was successful and userProfile has an _id */}
           {(isUserSuccess && userProfile && userProfile._id && (<Sidebar /> as React.ReactNode))}
            <div
              style={{
                flexGrow: 1,
                height: '100vh', // Ensure the content area can scroll independently
                overflow: 'auto', // Allows content to scroll
                // Removed position: 'absolute' and related top, left, right, bottom, width properties
              }}
            >
              <Suspense fallback={<LoadingFallback />}>
                <Routes>
                  {/* Pass userProfile to ProtectedRoutes */}
                  <Route path="/media/slug/:slug" element={<ProtectedRoute element={<MediaDetail />} />} />
                  <Route path="/media-library" element={<ProtectedRoute element={<MediaLibraryPage />} />} />
                  <Route path="/account" element={<ProtectedRoute element={<Account />} />} />
                  <Route path="/user/:id" element={<ProtectedRoute element={<UserPage />} />} />
                  <Route path="/admin-users" element={<ProtectedRoute element={<AccountUsers />} adminOnly />} />
                  <Route path="/admin-tags" element={<ProtectedRoute element={<AccountTags />} adminOnly />} />
                  <Route path="/admin-media-types" element={<ProtectedRoute element={<AccountMediaTypes />} adminOnly />} />
                  {/* Conditional route based on userProfile role */}
                  {(userProfile as User | null | undefined)?.role === 'superAdmin' && (
                    <Route path="/manage-media-types" element={<ProtectedRoute element={<AccountMediaTypes />} adminOnly />} />
                  )}
                  <Route path="/admin-dashboard" element={<ProtectedRoute element={<AccountAdminDashboard />} adminOnly />} />
                  <Route path="/accept-invitation/:token" element={<AcceptInvitation />} /> {/* Consider if this needs protection */}
                  <Route path="/home" element={<ProtectedRoute element={<Home />} />} />
                  <Route path="/password-setup" element={<ProtectedRoute element={<PasswordSetupPage />} />} />
                  <Route path="/style-guide" element={<ProtectedRoute element={<StyleGuidePage />} adminOnly />} />
                  <Route path="/login" element={<AuthPage />} />
                  <Route path="/" element={<AuthPage />} />
                  {/* <Route path="/admin-media-approvals" element={<ProtectedRoute element={<MediaApprovalPage />} adminOnly />} /> */}
                </Routes>
              </Suspense>
            </div>
          </div>
        </Router>
      </ThemeContext.Provider>

      {/* Centralized ToastContainer for the entire application */}
      <ToastContainer
        position={isMobile ? "bottom-center" : "top-right"}
        autoClose={3000}
        hideProgressBar={false}
        newestOnTop={true}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss={false}
        draggable
        pauseOnHover
        limit={3}  // Limit concurrent notifications
      />
    </ThemeProvider>
  );
};

// Main App component that provides the QueryClientProvider
const App: React.FC = () => {
  return (
    <QueryClientProvider client={queryClient}>
      <AppContent />
      {/* Only show React Query Devtools in development */}
      {process.env.NODE_ENV === 'development' && <ReactQueryDevtools initialIsOpen={false} />}
    </QueryClientProvider>
  );
};

export default App;

import React, { useState } from 'react';
import { 
  Paper, 
  Typography, 
  Box, 
  Card, 
  CardContent,
  LinearProgress,
  Tooltip,
  IconButton,
  Divider
} from '@mui/material';
import RefreshIcon from '@mui/icons-material/Refresh';
import StorageIcon from '@mui/icons-material/Storage';
import CloudIcon from '@mui/icons-material/Cloud';
import GroupIcon from '@mui/icons-material/Group';
import CollectionsIcon from '@mui/icons-material/Collections';
import CategoryIcon from '@mui/icons-material/Category';
import TagIcon from '@mui/icons-material/Tag';
import { formatFileSize } from '../../utils/formatFileSize';
import { useDatabaseStats, useUserProfile } from '../../hooks/query-hooks';

const DatabaseStats: React.FC = () => {
  const [_error, setError] = useState<string | null>(null);

  // Get userProfile for enabling the query
  const { data: userProfile } = useUserProfile();

  // Use TanStack Query for fetching database stats, passing userProfile
  const { 
    data: stats, 
    isLoading, 
    isError, 
    error: queryError,
    refetch 
  } = useDatabaseStats(userProfile);

  // Create mock data function for fallback
  const createMockData = () => {
    // Mock data should be self-contained or use minimal external dependencies if Redux is removed
    // For now, this will likely break or return less accurate mock data without Redux state.
    // Consider removing or refactoring mock data if live data and loading/error states are sufficient.
    const mockTags = new Set<string>();
    // allMedia.forEach(media => { // allMedia from Redux is removed
    //   if (media.metadata?.tags && Array.isArray(media.metadata.tags)) {
    //     media.metadata.tags.forEach(tag => mockTags.add(tag));
    //   }
    // });

    return {
      totalUsers: 2, // Was mongoUsers.length || 2
      activeUsers: 1, // Was Math.max(1, Math.floor(mongoUsers.length / 2)) || 1
      totalMediaFiles: 0, // Was allMedia.length
      totalMediaTypes: 0, // Was mediaTypes.length
      totalTags: mockTags.size,
      storageUsed: 0, // Was allMedia.reduce(...)
      storageLimit: 5 * 1024 * 1024 * 1024, // 5GB
      dbSize: 256 * 1024 * 1024, // 256MB
      lastBackup: new Date(Date.now() - 1000 * 60 * 60 * 24).toISOString(),
      uptime: 15 * 24 * 60 * 60 // 15 days in seconds
    };
  };

  // Handle refresh button click
  const handleRefresh = () => {
    refetch();
  };

  // Format uptime from seconds to days, hours, minutes
  const formatUptime = (seconds: number) => {
    const days = Math.floor(seconds / (24 * 60 * 60));
    const hours = Math.floor((seconds % (24 * 60 * 60)) / (60 * 60));
    const minutes = Math.floor((seconds % (60 * 60)) / 60);

    return `${days}d ${hours}h ${minutes}m`;
  };

  // Show loading state
  if (isLoading) {
    return (
      <Paper elevation={2} className="dashboard-card">
        <Typography variant="h6" gutterBottom>Database Statistics</Typography>
        <Box sx={{ width: '100%', mt: 2 }}>
          <LinearProgress />
        </Box>
      </Paper>
    );
  }

  // Show error state with fallback data
  if (isError) {
    setError(queryError instanceof Error ? queryError.message : 'Failed to load database stats');

    // If we have no stats data, create mock data
    if (!stats) {
      const mockStats = createMockData();

      // Render the component with mock data
      // (We continue to the main return statement and use mockStats instead)
      return renderDashboard(mockStats, true);
    }
  }

  // If we still don't have stats after error handling, return nothing
  if (!stats) return null;

  // Render the dashboard with real data
  return renderDashboard(stats, isLoading);

  // Helper function to render the dashboard with given stats
  function renderDashboard(statsData: any, isLoadingState: boolean) {
    // Calculate storage usage percentage
    const storagePercentage = (statsData.storageUsed / statsData.storageLimit) * 100;

    return (
      <Paper elevation={2} className="dashboard-card">
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
          <Typography variant="h6">Database Statistics</Typography>
          <Tooltip title="Refresh">
            <IconButton onClick={handleRefresh} size="small" color="primary">
              <RefreshIcon />
            </IconButton>
          </Tooltip>
        </Box>

        {isLoadingState && (
          <Box sx={{ width: '100%', mb: 2 }}>
            <LinearProgress />
          </Box>
        )}

        <div className="grid-container" style={{ gridTemplateRows: 'auto auto', minHeight: '450px' }}>
          {/* Storage Usage */}
          <div className="grid-item" style={{ gridColumn: 'span 12' }}>
            <Card>
              <CardContent>
                <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                  <StorageIcon color="primary" sx={{ mr: 1 }} />
                  <Typography variant="subtitle1" fontWeight="medium">Storage Usage</Typography>
                </Box>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                  <Typography variant="body2" color="text.secondary">
                    {formatFileSize(statsData.storageUsed)} used of {formatFileSize(statsData.storageLimit)}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    {storagePercentage.toFixed(1)}%
                  </Typography>
                </Box>
                <Box sx={{ width: '100%', height: 8, bgcolor: 'background.paper', borderRadius: 4, overflow: 'hidden' }}>
                  <Box 
                    sx={{ 
                      width: `${storagePercentage}%`, 
                      height: '100%', 
                      bgcolor: storagePercentage > 90 ? 'error.main' : storagePercentage > 70 ? 'warning.main' : 'success.main',
                      borderRadius: 4
                    }} 
                  />
                </Box>
              </CardContent>
            </Card>
          </div>

          <div className="cards-grid" style={{ 
            display: 'grid', 
            gridTemplateColumns: 'repeat(3, 1fr)', 
            gap: '16px',
            marginTop: '16px'
          }}>
            {/* Database Metrics */}
            <Card sx={{ height: '100%' }}>
              <CardContent>
                <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                  <CloudIcon color="info" sx={{ mr: 1 }} />
                  <Typography variant="subtitle1" fontWeight="medium">Database</Typography>
                </Box>

                <Box sx={{ mb: 1 }}>
                  <Typography variant="body2" color="text.secondary">Database Size</Typography>
                  <Typography variant="h6">{formatFileSize(statsData.dbSize)}</Typography>
                </Box>

                <Box sx={{ mb: 1 }}>
                  <Typography variant="body2" color="text.secondary">Last Backup</Typography>
                  <Typography variant="h6">
                    {statsData.lastBackup ? new Date(statsData.lastBackup).toLocaleDateString() : 'Never'}
                  </Typography>
                </Box>

                <Box>
                  <Typography variant="body2" color="text.secondary">Uptime</Typography>
                  <Typography variant="h6">{formatUptime(statsData.uptime)}</Typography>
                </Box>
              </CardContent>
            </Card>

            {/* Users */}
            <Card sx={{ height: '100%' }}>
              <CardContent>
                <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                  <GroupIcon color="success" sx={{ mr: 1 }} />
                  <Typography variant="subtitle1" fontWeight="medium">Users</Typography>
                </Box>

                <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', mb: 2 }}>
                  <Typography variant="body1">Total Users</Typography>
                  <Typography variant="h5" fontWeight="medium">{statsData.totalUsers}</Typography>
                </Box>

                <Divider sx={{ my: 1 }} />

                <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
                  <Typography variant="body1">Active Users</Typography>
                  <Typography variant="h5" fontWeight="medium">{statsData.activeUsers}</Typography>
                </Box>
              </CardContent>
            </Card>

            {/* Content */}
            <Card sx={{ height: '100%' }}>
              <CardContent>
                <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                  <CollectionsIcon color="secondary" sx={{ mr: 1 }} />
                  <Typography variant="subtitle1" fontWeight="medium">Content</Typography>
                </Box>

                <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                  <Box sx={{ display: 'flex', alignItems: 'center' }}>
                    <CollectionsIcon fontSize="small" sx={{ mr: 0.5, opacity: 0.7 }} />
                    <Typography variant="body2">Media Files</Typography>
                  </Box>
                  <Typography variant="body1" fontWeight="medium">{statsData.totalMediaFiles}</Typography>
                </Box>

                <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                  <Box sx={{ display: 'flex', alignItems: 'center' }}>
                    <CategoryIcon fontSize="small" sx={{ mr: 0.5, opacity: 0.7 }} />
                    <Typography variant="body2">Media Types</Typography>
                  </Box>
                  <Typography variant="body1" fontWeight="medium">{statsData.totalMediaTypes}</Typography>
                </Box>

                <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
                  <Box sx={{ display: 'flex', alignItems: 'center' }}>
                    <TagIcon fontSize="small" sx={{ mr: 0.5, opacity: 0.7 }} />
                    <Typography variant="body2">Tags</Typography>
                  </Box>
                  <Typography variant="body1" fontWeight="medium">{statsData.totalTags}</Typography>
                </Box>
              </CardContent>
            </Card>
          </div>
        </div>
      </Paper>
    );
  }
};

export default DatabaseStats;

import React from 'react';
import { Paper, Typography, Box, CircularProgress } from '@mui/material';
import { PieChart, Pie, Cell, ResponsiveContainer, Tooltip, Legend } from 'recharts';
import { useTransformedMedia, useMediaTypesWithUsageCounts, TransformedMediaFile, useUserProfile } from '../../hooks/query-hooks';

const COLORS: Record<string, string> = {
  'Product Image': '#3f8cff',
  'Webinar Video': '#8bc34a',
  'Protocol': '#ff4081',
  'Application Note': '#ffc107',
  'Application Header Image': '#03a9f4'
};

const DEFAULT_COLORS = [
  '#3f8cff', // blue
  '#ffc107', // amber
  '#8bc34a', // light green
  '#ff4081', // pink
  '#03a9f4', // light blue
  '#9c27b0', // purple
  '#f44336', // red
  '#4caf50', // green
  '#ff9800', // orange
  '#2196f3', // blue
];

// Custom legend with proper spacing
const CustomLegend = (props: any) => {
  const { payload } = props;

  return (
    <ul style={{ 
      display: 'flex', 
      flexWrap: 'wrap', 
      justifyContent: 'center',
      padding: 0,
      margin: '10px 0 0 0',
      listStyle: 'none'
    }}>
      {payload.map((entry: any, index: number) => (
        <li 
          key={`legend-item-${index}`} 
          style={{ 
            display: 'flex', 
            alignItems: 'center', 
            marginRight: 20,
            marginBottom: 8,
            fontSize: '12px',
            color: 'var(--text-primary)'
          }}
        >
          <span style={{ 
            display: 'block',
            width: 10,
            height: 10,
            borderRadius: '50%',
            backgroundColor: entry.color,
            marginRight: 8
          }} />
          {entry.value}
        </li>
      ))}
    </ul>
  );
};

// Custom tooltip component with typescript
interface CustomTooltipProps {
  active?: boolean;
  payload?: Array<any>;
}

const CustomTooltip: React.FC<CustomTooltipProps> = ({ active, payload }) => {
  if (active && payload && payload.length) {
    const data = payload[0];
    const totalCount = data.payload.totalCount || 1;
    const percentage = ((data.value / totalCount) * 100).toFixed(1);

    return (
      <div style={{ 
        backgroundColor: '#333',
        padding: '10px',
        border: 'none',
        borderRadius: '4px', 
        boxShadow: '0 2px 8px rgba(0, 0, 0, 0.5)'
      }}>
        <p style={{ 
          color: '#fff', 
          fontWeight: 'bold',
          margin: '0 0 5px 0',
          fontSize: '13px'
        }}>{data.name}</p>
        <p style={{ 
          color: 'var(--text-primary)',
          margin: '0',
          fontSize: '12px' 
        }}>
          <span style={{ marginRight: '6px' }}>{data.value} files</span>
          <span style={{ 
            color: 'var(--text-primary)',
            fontSize: '11px'
          }}>({percentage}%)</span>
        </p>
      </div>
    );
  }

  return null;
};

const MediaTypeDistribution: React.FC = () => {
  const { data: userProfile, isLoading: isLoadingUserProfile } = useUserProfile();

  const { 
    data: allMedia = [], 
    isLoading: isLoadingMedia, 
    isError: isMediaError 
  } = useTransformedMedia(userProfile);

  const { 
    data: mediaTypes = [], 
    isLoading: isLoadingMediaTypes, 
    isError: isMediaTypesError 
  } = useMediaTypesWithUsageCounts(userProfile);

  // Loading state
  if (isLoadingUserProfile || isLoadingMedia || isLoadingMediaTypes) {
    return (
      <Paper elevation={2} className="dashboard-card media-types-chart">
        <Typography variant="h6" gutterBottom>Media Type Distribution</Typography>
        <Box sx={{ 
          display: 'flex', 
          justifyContent: 'center', 
          alignItems: 'center', 
          height: '300px' 
        }}>
          <CircularProgress />
        </Box>
      </Paper>
    );
  }

  // Error state
  if (isMediaError || isMediaTypesError) {
    return (
      <Paper elevation={2} className="dashboard-card media-types-chart">
        <Typography variant="h6" gutterBottom>Media Type Distribution</Typography>
        <Box sx={{ 
          display: 'flex', 
          justifyContent: 'center', 
          alignItems: 'center', 
          height: '300px' 
        }}>
          <Typography variant="body1" color="error">
            Error loading data. Please try refreshing the page.
          </Typography>
        </Box>
      </Paper>
    );
  }

  // Calculate distribution data
  const getDistributionData = () => {
    // Create a count map of media types
    const mediaTypeCounts = allMedia.reduce((acc: {[key: string]: number}, media: TransformedMediaFile) => {
      const type = media.mediaType || 'Uncategorized';
      acc[type] = (acc[type] || 0) + 1;
      return acc;
    }, {});

    // Convert to array format for the chart and assign consistent colors
    return Object.entries(mediaTypeCounts).map(([name, value], index) => ({
      name,
      value,
      totalCount: allMedia.length, // Add total count to each item for percentage calculation
      // Use predefined color if available, otherwise use color from mediaTypes or default colors
      color: COLORS[name] || mediaTypes.find(type => type.name === name)?.catColor || DEFAULT_COLORS[index % DEFAULT_COLORS.length]
    })).sort((a, b) => b.value - a.value); // Sort by count descending
  };

  const data = getDistributionData();

  // Empty state
  if (data.length === 0) {
    return (
      <Paper elevation={2} className="dashboard-card media-types-chart">
        <Typography variant="h6" gutterBottom>Media Type Distribution</Typography>
        <Box sx={{ 
          display: 'flex', 
          justifyContent: 'center', 
          alignItems: 'center', 
          height: '300px' 
        }}>
          <Typography variant="body1" color="textSecondary">
            No media files available to display distribution.
          </Typography>
        </Box>
      </Paper>
    );
  }

  return (
    <div className="dashboard-grid" style={{ 
      display: 'grid',
      gridTemplateColumns: 'repeat(12, 1fr)',
      gap: '1.25rem',
      marginBottom: '1.25rem'
    }}>
      <Paper 
        elevation={2} 
        className="dashboard-card media-types-chart" 
        style={{ gridColumn: 'span 7' }}
      >
        <Typography variant="h6" sx={{ mb: 1 }}>Media Type Distribution</Typography>
        <Box sx={{ 
          width: '100%', 
          height: 'calc(100% - 40px)', 
          minHeight: '350px',
          position: 'relative'
        }}>
          <ResponsiveContainer width="100%" height="100%" minHeight={350}>
            <PieChart 
              width={500} 
              height={350} 
              margin={{ top: 20, right: 20, bottom: 40, left: 20 }}
            >
              <Pie
                data={data}
                cx="50%"
                cy="45%"
                labelLine={false}
                outerRadius={110}
                innerRadius={60}
                paddingAngle={2}
                cornerRadius={3}
                dataKey="value"
                stroke="rgba(0, 0, 0, 0.2)"
                strokeWidth={1}
                // Add labels directly to the Pie component
                label={({ 
                  cx, cy, midAngle, outerRadius, percent, name, index
                }: any) => {
                  if (index >= 5 || percent < 0.08) return null;  // Only show for top 5 and significant slices
                  const RADIAN = Math.PI / 180;
                  const radius = outerRadius * 1.1;
                  const x = cx + radius * Math.cos(-midAngle * RADIAN);
                  const y = cy + radius * Math.sin(-midAngle * RADIAN);

                  return (
                    <text
                      x={x}
                      y={y}
                      fill="var(--color-text-primary)"
                      textAnchor={x > cx ? 'start' : 'end'}
                      dominantBaseline="central"
                      fontSize={12}
                    >
                      {`${name} ${(percent * 100).toFixed(0)}%`}
                    </text>
                  );
                }}
              >
                {data.map((entry, index) => (
                  <Cell 
                    key={`cell-${index}`} 
                    fill={entry.color} 
                  />
                ))}
              </Pie>
              <Tooltip content={<CustomTooltip />} />
              <Legend 
                content={<CustomLegend />}
                layout="horizontal"
                verticalAlign="bottom"
                align="center"
              />
            </PieChart>
          </ResponsiveContainer>
        </Box>
      </Paper>

      <Paper 
        elevation={2} 
        className="dashboard-card has-scroll" 
        style={{ gridColumn: 'span 5', padding: '1rem' }}
      >
        <Box sx={{ 
          display: 'flex', 
          flexDirection: 'column', 
          height: '100%'
        }}>
          {/* Total Files Header */}
          <Box sx={{ 
            mb: 2, 
            pb: 2, 
            borderBottom: '1px solid rgba(255, 255, 255, 0.1)',
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'flex-end'
          }}>
            <Box>
              <Typography variant="subtitle2" color="textSecondary" sx={{ fontSize: '0.75rem' }}>
                Total Files
              </Typography>
              <Typography variant="h4" color="primary" sx={{ mt: 0.5, fontWeight: 600 }}>
                {allMedia.length}
              </Typography>
            </Box>

            <Box sx={{ textAlign: 'right' }}>
              <Typography variant="subtitle2" color="textSecondary" sx={{ fontSize: '0.75rem' }}>
                Media Types
              </Typography>
              <Typography variant="h6" sx={{ mt: 0.5 }}>
                {data.length}
              </Typography>
            </Box>
          </Box>

          {/* Media Types List */}
          <Box sx={{ flexGrow: 1, overflow: 'auto' }}>
            {data.map((item, index) => {
              const percentage = allMedia.length ? (item.value / allMedia.length * 100) : 0;

              return (
                <Box 
                  key={index} 
                  sx={{ 
                    mb: 1.25,
                    '&:last-child': { mb: 0 } 
                  }}
                >
                  <Box sx={{ 
                    display: 'flex', 
                    justifyContent: 'space-between',
                    alignItems: 'center',
                    mb: 0.5 
                  }}>
                    <Box sx={{ display: 'flex', alignItems: 'center', maxWidth: '75%' }}>
                      <Box 
                        sx={{ 
                          width: 8, 
                          height: 8, 
                          borderRadius: '50%', 
                          bgcolor: item.color,
                          flexShrink: 0,
                          mr: 1
                        }} 
                      />
                      <Typography 
                        variant="body2" 
                        sx={{ 
                          fontSize: '0.85rem',
                          fontWeight: index === 0 ? 600 : 400,
                          whiteSpace: 'nowrap',
                          overflow: 'hidden',
                          textOverflow: 'ellipsis'
                        }}
                      >
                        {item.name}
                      </Typography>
                    </Box>
                    <Typography 
                      variant="body2" 
                      sx={{ 
                        fontWeight: index === 0 ? 600 : 400,
                        fontSize: '0.85rem'
                      }}
                    >
                      {item.value}
                    </Typography>
                  </Box>

                  {/* Progress Bar */}
                  <Box 
                    sx={{ 
                      width: '100%', 
                      height: 4, 
                      bgcolor: 'rgba(255, 255, 255, 0.1)', 
                      borderRadius: 2,
                      overflow: 'hidden'
                    }}
                  >
                    <Box 
                      sx={{ 
                        width: `${percentage}%`, 
                        height: '100%', 
                        bgcolor: item.color,
                        borderRadius: 2 
                      }} 
                    />
                  </Box>

                  {/* Percentage */}
                  <Typography 
                    variant="caption" 
                    sx={{ 
                      color: 'text.secondary',
                      fontSize: '0.7rem',
                      mt: 0.25,
                      display: 'block',
                      textAlign: 'right'
                    }}
                  >
                    {percentage.toFixed(1)}%
                  </Typography>
                </Box>
              );
            })}
          </Box>
        </Box>
      </Paper>
    </div>
  );
};

