export const MediaDetailPreview: React.FC<MediaDetailPreviewProps> = ({ 
  mediaFile, 
  onEdit, 
  onDownload, 
  isEditingEnabled,
  onThumbnailUpdate
}) => {
  // Get uploadedBy from either direct property or metadata
  const isMobile = useMediaQuery((theme: Theme) => theme.breakpoints.down('sm'));

  // Add state for the active tab
  const [activeTab, setActiveTab] = useState(0);

  // Check if there are related media files
  const hasRelatedMedia = mediaFile.metadata?.relatedMedia && 
    (Array.isArray(mediaFile.metadata.relatedMedia) ? 
      mediaFile.metadata.relatedMedia.length > 0 : 
      mediaFile.metadata.relatedMedia.mediaId);

  // Check if this is a video file
  const isVideo = mediaFile.fileExtension && 
    ['mp4', 'webm', 'ogg', 'mov'].includes(mediaFile.fileExtension.toLowerCase());

  // Handler for tab changes
  const handleTabChange = (_event: React.SyntheticEvent, newValue: number) => {
    setActiveTab(newValue);
  };

  // Component to render media content based on file type
  const RenderMediaContent = React.useCallback(() => {
    if (!mediaFile) return null;

    if (mediaFile.fileExtension &&
      ["jpg", "jpeg", "png", "gif", "webp", "svg", "bmp"].includes(
        mediaFile.fileExtension.toLowerCase()
      )) {
      // Image preview
      return (
        <img
          src={mediaFile.location}
          alt={mediaFile.metadata?.altText || ""}
        />
      );
    } else if (mediaFile.fileExtension &&
      ["mp4", "webm", "ogg", "mov"].includes(
        mediaFile.fileExtension.toLowerCase()
      )) {
      // Video preview
      return (
        <Box
          sx={{
            width: "100%",
            height: "100%",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            background: "#000",
          }}
        >
          <video
            controls
            autoPlay={false}
            style={{
              width: "100%",
              maxHeight: isMobile ? "300px" : "600px",
            }}
            poster={mediaFile.metadata?.v_thumbnail || undefined}
          >
            <source
              src={mediaFile.location}
              type={`video/${
                mediaFile.fileExtension === "mov"
                  ? "quicktime"
                  : mediaFile.fileExtension.toLowerCase()
              }`}
            />
            Your browser does not support the video tag.
          </video>
        </Box>
      );
    } else if (mediaFile.fileExtension &&
      ["mp3", "wav", "ogg", "m4a"].includes(
        mediaFile.fileExtension.toLowerCase()
      )) {
      // Audio preview
      return (
        <Box
          sx={{
            p: 4,
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            gap: 2,
          }}
        >
          <FaFileAudio size={isMobile ? 48 : 64} />
          <audio
            controls
            src={mediaFile.location}
            style={{ width: "100%" }}
          >
            Your browser does not support the audio element.
          </audio>
        </Box>
      );
    } else if (mediaFile.fileExtension &&
      ["pdf"].includes(mediaFile.fileExtension.toLowerCase())) {
      // PDF preview
      return (
        <Box
          sx={{
            width: "100%",
            height: "100%",
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
            p: 2,
            position: "relative", // Add for debugging overlay
          }}
        >
          {/* Primary method: Try using an iframe for preview */}
          <iframe
            src={`${mediaFile.location}#toolbar=1&navpanes=1&scrollbar=1`}
            style={{
              width: "100%",
              height: isMobile ? "400px" : "600px",
              border: "none",
              borderRadius: "4px",
              overflow: "hidden",
            }}
            title="PDF Preview"
            onError={(e) => {
              // Show alternative PDF viewer on error
              const iframe = e.target as HTMLIFrameElement;
              if (iframe && iframe.style) {
                iframe.style.display = 'none';
              }

              // Try to show the object tag fallback
              const objectFallback = document.querySelector('.pdf-object-fallback') as HTMLElement;
              if (objectFallback && objectFallback.style) {
                objectFallback.style.display = 'block';

                // If object also fails, show icon fallback
                setTimeout(() => {
                  const objectElement = objectFallback.querySelector('object');
                  if (objectElement && !objectElement.contentDocument) {
                    objectFallback.style.display = 'none';
                    const iconFallback = document.querySelector('.pdf-icon-fallback') as HTMLElement;
                    if (iconFallback && iconFallback.style) {
                      iconFallback.style.display = 'flex';
                    }
                  }
                }, 1000);
              }
            }}
          />

          {/* Object tag fallback - works in some browsers where iframe doesn't */}
          <Box 
            className="pdf-object-fallback"
            sx={{ 
              display: 'none',
              width: '100%',
              height: isMobile ? "400px" : "600px",
            }}
          >
            <object
              data={mediaFile.location}
              type="application/pdf"
              width="100%"
              height="100%"
              style={{
                border: "none",
                borderRadius: "4px",
              }}
            >
              <p>Your browser does not support PDF viewing.</p>
            </object>
          </Box>

          {/* Icon fallback as last resort */}
          <Box 
            className="pdf-icon-fallback"
            sx={{ 
              display: 'none',
              flexDirection: 'column',
              alignItems: 'center',
              justifyContent: 'center',
              p: 3,
              gap: 2
            }}
          >
            <FaFilePdf size={isMobile ? 56 : 86} color="#ef4444" />
            <Typography variant={isMobile ? "body2" : "body1"} sx={{ mt: 2, textAlign: 'center' }}>
              PDF preview not available in this browser.
            </Typography>
            <Button
              variant="contained"
              color="primary"
              startIcon={<FaDownload />}
              onClick={onDownload}
              size={isMobile ? "small" : "medium"}
              sx={{ mt: 2 }}
            >
              Download PDF
            </Button>
          </Box>
        </Box>
      );
    } else if (mediaFile.fileExtension &&
      ["doc", "docx"].includes(mediaFile.fileExtension.toLowerCase())) {
      // Word document
      return (
        <Box
          sx={{
            p: 4,
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            gap: 2,
          }}
        >
          <FaFileWord size={isMobile ? 48 : 64} />
          <Typography variant={isMobile ? "body2" : "body1"}>
            Microsoft Word Document Preview Not Available
          </Typography>
        </Box>
      );
    } else if (mediaFile.fileExtension &&
      ["xls", "xlsx"].includes(mediaFile.fileExtension.toLowerCase())) {
      // Excel document
      return (
        <Box
          sx={{
            p: 4,
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            gap: 2,
          }}
        >
          <FaFileExcel size={isMobile ? 48 : 64} />
          <Typography variant={isMobile ? "body2" : "body1"}>
            Microsoft Excel Document Preview Not Available
          </Typography>
        </Box>
      );
    } else {
      // Generic file
      return (
        <Box
          sx={{
            p: 4,
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            gap: 2,
          }}
        >
          <FaFile size={isMobile ? 48 : 64} />
          <Typography variant={isMobile ? "body2" : "body1"}>
            {mediaFile.fileExtension
              ? `${mediaFile.fileExtension.toUpperCase()} File Preview Not Available`
              : "File Preview Not Available"}
          </Typography>
        </Box>
      );
    }
  }, [mediaFile, isMobile, onDownload]);

  // Inside the MediaDetailPreview component, after the RenderMediaContent component definition
  // Add a new piece of state to track the content height
  const [contentHeight, setContentHeight] = useState<number>(400); // Default min height
  const contentRef = useRef<HTMLDivElement>(null);

  // Use ResizeObserver to track content height changes
  useEffect(() => {
    if (!contentRef.current) return;

    // Function to measure height
    const updateHeight = () => {
      if (contentRef.current && activeTab === 0) {
        const height = contentRef.current.offsetHeight;
        if (height > 200) { // Only update if it's a meaningful height
          setContentHeight(height);
        }
      }
    };

    // Initial measurement
    updateHeight();

    // Set up ResizeObserver for dynamic updates
    const resizeObserver = new ResizeObserver((entries) => {
      for (const entry of entries) {
        if (entry.target === contentRef.current && activeTab === 0) {
          updateHeight();
        }
      }
    });

    // Start observing
    resizeObserver.observe(contentRef.current);

    // Cleanup
    return () => {
      resizeObserver.disconnect();
    };
  }, [activeTab, mediaFile]);

  // Media type specific adjustment: ensure proper height for different media types
  useEffect(() => {
    if (!mediaFile) return;

    // Set larger minimum height for certain file types
    if (mediaFile.fileExtension) {
      const ext = mediaFile.fileExtension.toLowerCase();

      // Videos and PDFs need more height
      if (['mp4', 'webm', 'ogg', 'mov', 'pdf'].includes(ext)) {
        setContentHeight(prev => Math.max(prev, 600));
      }
      // Images can vary
      else if (['jpg', 'jpeg', 'png', 'gif', 'webp'].includes(ext)) {
        // Don't set a fixed height, let the image determine it
      }
      // Other file types need less space
      else {
        setContentHeight(prev => Math.max(prev, 300));
      }
    }
  }, [mediaFile]);

  return (
    <Box className="media-preview">
      <Box className="media-preview-header">
        <Typography 
          variant={isMobile ? "h5" : "h4"} 
          gutterBottom 
          style={{ 
            color: 'var(--accent-color)',
            fontSize: isMobile ? '1.25rem' : '2rem',
            marginBottom: '0',
          }}
        >
          {getMetadataField(mediaFile, 'fileName') || mediaFile.title || 'Untitled Media'}
        </Typography>

        {/* Action Buttons */}
        <Box sx={{ 
          display: 'flex',
          alignItems: 'center',
          gap: '8px'
        }}>
          <Button
            variant="contained"
            color="primary"
            startIcon={<FaDownload />}
            onClick={onDownload}
            size={isMobile ? "small" : "medium"}
            sx={{ 
              padding: isMobile ? '4px 8px' : '6px 12px',
              minWidth: isMobile ? 'auto' : '64px'
            }}
          >
            {isMobile ? '' : 'Download'}
          </Button>

          {isEditingEnabled && onEdit && (
            <Button
              variant="outlined"
              color="primary"
              startIcon={<EditIcon />}
              onClick={(e) => {
                console.log('Edit button clicked');
                e.stopPropagation();
                if (onEdit) onEdit();
              }}
              size={isMobile ? "small" : "medium"}
              sx={{ 
                padding: isMobile ? '4px 8px' : '6px 12px',
                minWidth: isMobile ? 'auto' : '64px',
                zIndex: 10  // Add a higher z-index to ensure it's clickable
              }}
            >
              {isMobile ? '' : 'Edit'}
            </Button>
          )}

          {/* Add thumbnail button for video files */}
          {isEditingEnabled && isVideo && onThumbnailUpdate && (
            <Tooltip title="Update Video Thumbnail">
              <Button
                variant="outlined"
                color="secondary"
                startIcon={<PhotoIcon />}
                onClick={onThumbnailUpdate}
                size={isMobile ? "small" : "medium"}
                sx={{ 
                  padding: isMobile ? '4px 8px' : '6px 12px',
                  minWidth: isMobile ? 'auto' : '64px',
                }}
              >
                {isMobile ? '' : 'Thumbnail'}
              </Button>
            </Tooltip>
          )}
        </Box>
      </Box>

      {/* Tabs only shown if related media exists */}
      {hasRelatedMedia ? (
        <>
          <Box sx={{ borderBottom: 1, borderColor: 'divider', mt: 2 }}>
            <Tabs value={activeTab} onChange={handleTabChange} aria-label="media content tabs">
              <Tab label="Main File" id="tab-0" aria-controls="tabpanel-0" />
              <Tab label="Related Files" id="tab-1" aria-controls="tabpanel-1" />
            </Tabs>
          </Box>

          {/* Shared container with minimum height */}
          <Box 
            className="tab-content-container"
            sx={{ 
              mt: 2, 
              minHeight: contentHeight ? `${contentHeight}px` : 'auto',
              transition: 'min-height 0.3s ease-in-out'
            }}
          >
            {/* Main File Tab Content */}
            <Box 
              className="tab-panel"
              role="tabpanel"
              hidden={activeTab !== 0}
              id="tabpanel-0"
              aria-labelledby="tab-0"
            >
              {activeTab === 0 && (
                <Box className="media-preview-media" ref={contentRef}>
                  <RenderMediaContent />
                </Box>
              )}
            </Box>

            {/* Related Files Tab Content */}
            <Box 
              className="tab-panel related-tab"
              role="tabpanel"
              hidden={activeTab !== 1}
              id="tabpanel-1"
              aria-labelledby="tab-1"
              sx={{ 
                height: contentHeight ? `${contentHeight}px` : 'auto',
                overflowY: 'auto'
              }}
            >
              {activeTab === 1 && (
                <Box className="related-media-container">
                  <Box className="related-media-list">
                    {/* Render related media */}
                    {Array.isArray(mediaFile.metadata?.relatedMedia) ? (
                      mediaFile.metadata.relatedMedia.map((item, index) => (
                        <RelatedMediaItem key={index} media={item} />
                      ))
                    ) : (
                      // Single object case
                      mediaFile.metadata?.relatedMedia?.mediaId && (
                        <RelatedMediaItem media={mediaFile.metadata.relatedMedia} />
                      )
                    )}
                  </Box>
                </Box>
              )}
            </Box>
          </Box>
        </>
      ) : (
        // No tabs, just show the main media
        <Box className="media-preview-media" sx={{ mt: 2 }}>
          <RenderMediaContent />
        </Box>
      )}
    </Box>
  );
};

const MediaDetail: React.FC = () => {
  const { slug } = useParams<{ slug: string }>();
  const navigate = useNavigate();
  const location = useLocation();
  const isMobile = useMediaQuery((theme: Theme) => theme.breakpoints.down("sm"));
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);
  const [isThumbnailDialogOpen, setIsThumbnailDialogOpen] = useState(false);

  const queryClient = useQueryClient();

  const { data: mediaTypes = [] } = useMediaTypes();

  const { data: userProfile, isLoading: isUserLoading, isError: isUserProfileError } = useUserProfile(); 
  const { 
    data: mediaFile, 
    isLoading: isLoadingMedia,
    isError: isMediaError,
    error: mediaError,
    refetch,
  } = useMediaDetail(userProfile, slug);

  const { mutateAsync: updateMediaMutation } = useUpdateMedia();
  const { 
    isLoading: isCheckingHealth, 
    isError: isHealthError, 
    error: healthError,
    refetch: recheckHealth 
  } = useApiHealth();

  // MOVED HOOKS UP: Define userId and call useUsername and useEffect before conditional returns.
  // Ensure userId is derived safely, defaulting to empty string if mediaFile or uploadedBy is not yet available.
  const userId = mediaFile ? (getMetadataField(mediaFile, 'uploadedBy', '') || '') : '';
  const { username: uploadedBy } = useUsername(userId);

  // Effect to update the URL if the slug in the URL doesn't match the actual slug
  useEffect(() => {
    if (mediaFile && slug && mediaFile.slug && slug !== mediaFile.slug) {
      if (process.env.NODE_ENV === 'development') {
        console.log(`Correcting URL: changing ${slug} to ${mediaFile.slug}`);
      }
      const newPath = location.pathname.replace(slug, mediaFile.slug);
      navigate(newPath, { replace: true });
    }
  }, [mediaFile, slug, navigate, location.pathname]);

  useEffect(() => {
      if (!slug) {
      navigate('/media-library');
    }
  }, [slug, navigate]);

  // Set CSS variables for theming (MOVED UP)
  useEffect(() => {
    if (mediaFile && mediaTypes && mediaTypes.length > 0) {
      const mediaTypeInfo = mediaTypes.find(
        (type) => type.name === mediaFile.mediaType
      );
      const accentColor = mediaTypeInfo?.catColor || '#4dabf5';

      const root = document.documentElement;
      root.style.setProperty('--accent-color', accentColor);

      return () => {
        root.style.setProperty('--accent-color', '#4dabf5'); // Reset
      };
    } else {
      const root = document.documentElement;
      root.style.setProperty('--accent-color', '#4dabf5'); // Default if no mediaFile/mediaTypes
      return () => { // Ensure a cleanup function is always returned
          root.style.setProperty('--accent-color', '#4dabf5');
      };
    }
  }, [mediaFile, mediaTypes]);

  // Determine if editing is enabled based on userProfile role
  const isEditingEnabled = !isUserLoading && userProfile && (userProfile.role === 'admin' || userProfile.role === 'superAdmin');

  // 1. Handle user profile loading state first
  if (isUserLoading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" height="100vh">
        <CircularProgress />
        <Typography sx={{ ml: 2 }}>Loading user information...</Typography>
      </Box>
    );
  }

  // 2. Handle user logged-out state (ProtectedRoute should also catch this)
  // This check is important because useMediaDetail is enabled based on userProfile.
  // If no userProfile, mediaFile will be undefined and isLoadingMedia will be false (as query is disabled).
  if (!userProfile) {
    // If there was an error fetching the profile itself, that's a different issue
    if (isUserProfileError) {
       return (
        <Box display="flex" flexDirection="column" justifyContent="center" alignItems="center" height="100vh" className="media-detail-container error-container">
          <Typography variant="h5" color="error" gutterBottom>Error Loading User Profile</Typography>
          <Typography variant="body1" color="textSecondary" sx={{ mb: 2 }}>Could not load your profile. Please try again.</Typography>
          <Button variant="contained" onClick={() => window.location.reload()}>Reload</Button>
        </Box>
      );
    }
    // Otherwise, user is simply not logged in.
    return (
      <Box display="flex" flexDirection="column" justifyContent="center" alignItems="center" height="100vh" className="media-detail-container error-container">
        <Typography variant="h5" color="textSecondary" gutterBottom>Access Denied</Typography>
        <Typography variant="body1" color="textSecondary" sx={{ mb: 2 }}>Please log in to view this content.</Typography>
        <Button variant="contained" onClick={() => navigate('/login')}>Go to Login</Button>
      </Box>
    );
  }

  // At this point, userProfile IS defined.
  // So, useMediaDetail query IS (or was) enabled (assuming slug is also present).

  // 3. Handle media loading state (query is enabled and fetching)
  if (isLoadingMedia) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" height="100vh">
        <CircularProgress />
        <Typography sx={{ ml: 2 }}>Loading media details...</Typography>
      </Box>
    );
  }

  // 4. Handle media fetching error (query was enabled, ran, but failed)
  if (isMediaError) {
    return (
      <div className="media-detail-container error-container">
        <Typography variant="h4" color="error" gutterBottom>Error Loading Media</Typography>
        <Typography variant="body1" sx={{ mb: 2 }}>
          {mediaError?.message || "An unexpected error occurred while trying to load the media file."}
        </Typography>
        <Typography variant="subtitle2">API Server Status: {isCheckingHealth ? "Checking..." : isHealthError ? `Offline (${healthError?.message || 'Unknown Error'})` : "Online"}</Typography>
        <Button onClick={() => recheckHealth()} disabled={isCheckingHealth} sx={{ my: 1 }}>Retry Health Check</Button>
        <Button onClick={() => refetch()} sx={{ my: 1, ml: 1 }}>Retry Loading Media</Button>

        <Box sx={{ mt: 2, p: 2, border: '1px dashed grey', borderRadius: '4px' }}>
          <Typography variant="caption" display="block" gutterBottom>Debug Information:</Typography>
          <Typography variant="caption" display="block">Slug: {slug}</Typography>
          <Typography variant="caption" display="block">Error: {mediaError?.message || JSON.stringify(mediaError)}</Typography>
        </Box>
      </div>
    );
  }

  // 5. Handle media not found (query was enabled, ran, succeeded, but returned no data e.g. 404 for slug)
  if (!mediaFile) {
    return (
      <div className="media-detail-container error-container">
        <Typography variant="h4" color="error" gutterBottom>Media Not Found</Typography>
        <Typography variant="body1">
          The requested media could not be found. It may have been moved or deleted.
        </Typography>
        <Button onClick={() => navigate('/media-library')} sx={{ mt: 2 }}>Back to Library</Button>
      </div>
    );
  }

  // If we reach here, userProfile exists, and mediaFile exists and there were no errors loading it.
  // const userId = getMetadataField(mediaFile, 'uploadedBy', '') || ''; // MOVED UP
  // const { username: uploadedBy } = useUsername(userId); // MOVED UP

  // Motion animation adjusted for mobile
  const motionProps = {
    initial: { opacity: 0, y: 20 },
    animate: { opacity: 1, y: 0 },
    transition: { duration: 0.3 }
  };

  // Define isVideo after mediaFile is guaranteed to be available
  const isVideo = mediaFile && mediaFile.fileExtension && 
  ['mp4', 'webm', 'ogg', 'mov'].includes(mediaFile.fileExtension.toLowerCase());

  const handleDownload = async () => {
    if (!mediaFile || !mediaFile.location) return;

    try {
      // Fetch file with responseType: 'blob' to get binary data
      const response = await axios.get(mediaFile.location, {
          responseType: 'blob'
        });

      // Create a blob URL for the file
        const blob = new Blob([response.data as BlobPart]);
        const url = window.URL.createObjectURL(blob);

      // Create a temporary link element and click it to download
        const link = document.createElement('a');
        link.href = url;
      link.download = mediaFile.metadata?.fileName || mediaFile.title || 'download';
        document.body.appendChild(link);
        link.click();

        // Clean up
        window.URL.revokeObjectURL(url);
        document.body.removeChild(link);

      toast.success('File download started');
    } catch (error) {
      console.error('Error downloading file:', error);
      toast.error('Failed to download file');
    }
  };

  const handleEdit = () => {
    setIsEditDialogOpen(true);
  };

  const handleSave = async (updatedMediaFile: Partial<MediaFileType> & { metadata?: Record<string, any> }): Promise<boolean> => {
    try {
      if (!mediaFile) {
        throw new Error('No media file data available to update');
      }

      // Track which fields are actually changed
      const changedFields: string[] = [];

      // Check if title changed
      if (updatedMediaFile.title && updatedMediaFile.title !== mediaFile.title) {
        changedFields.push('title');
      }

      // Check which metadata fields changed
      if (updatedMediaFile.metadata && mediaFile.metadata) {
        Object.keys(updatedMediaFile.metadata).forEach(key => {
          const oldValue = mediaFile.metadata?.[key];
          const newValue = updatedMediaFile.metadata?.[key];

          // Special handling for undefined to empty string conversion
          // This prevents registering a non-meaningful change when a field goes from undefined to empty string
          if (oldValue === undefined && (newValue === '' || newValue === null)) {
            // Skip adding this to changedFields since undefined to empty string is not a real change
            console.log(`Field ${key} changed from undefined to empty string, ignoring as non-meaningful change`);
            return;
          }

          // Only add to changedFields if the value actually changed
          if (JSON.stringify(oldValue) !== JSON.stringify(newValue)) {
            changedFields.push(`metadata.${key}`);
          }
        });
      }

      // Build the update payload - only include changed metadata fields
      const updatePayload: Partial<BaseMediaFile> & { 
        metadata?: Record<string, any>;
        changedFields?: string[];
      } = {
        _id: mediaFile._id,
        id: mediaFile.id,
        slug: mediaFile.slug || '',
        modifiedDate: mediaFile.modifiedDate || new Date().toISOString(),
        // Only include title if it changed
        ...(changedFields.includes('title') ? { title: updatedMediaFile.title } : {})
      };

      // Only include changed metadata fields
      if (updatedMediaFile.metadata && changedFields.some(f => f.startsWith('metadata.'))) {
        updatePayload.metadata = { ...(mediaFile.metadata || {}) }; // Start with all original fields

        changedFields
          .filter(field => field.startsWith('metadata.'))
          .forEach(field => {
            const metadataKey = field.replace('metadata.', '');
            // Only update the field in the payload IF the updatedMediaFile.metadata actually contains this key.
            // This prevents writing 'undefined' for fields that were simply omitted by the dialog's submission
            // but were part of the original mediaFile.metadata.
            if (updatedMediaFile.metadata!.hasOwnProperty(metadataKey)) {
              updatePayload.metadata![metadataKey] = updatedMediaFile.metadata![metadataKey];
            } else {
              // If the field was registered as "changed" because it's missing from updatedMediaFile.metadata
              // (i.e., original had a value, new submission doesn't for this key),
              // it implies the dialog did not intend to clear it but rather didn't include it in its submission.
              // The original value is already in updatePayload.metadata from the initial spread, so we don't
              // explicitly set it to undefined here. If a field needs to be explicitly cleared to null/undefined,
              // the dialog should send that value.
              if (process.env.NODE_ENV === 'development') {
                console.log(`DEBUG MediaDetail handleSave: Metadata field '${metadataKey}' was in changedFields but not in submitted metadata. Original value will be kept.`);
              }
            }
          });
      }

      // Add changedFields to the payload so the backend knows which fields were updated
      updatePayload.changedFields = changedFields;

      // Don't proceed if there are no changes to make
      if (changedFields.length === 0) {
        console.log('No changes detected, skipping update');
        return true;
      }

      // Use the mutation function
      await updateMediaMutation(updatePayload as any);

      // Handle successful update
      handleSuccessfulUpdate(updatePayload);

      return true;
    } catch (error: any) {
      handleFailedUpdate(error);
      return false;
    }
  };

  const handleSuccessfulUpdate = (_updatedMediaFile: Partial<BaseMediaFile> & { metadata?: Record<string, any> }) => {
    // Close the edit dialog 
    setIsEditDialogOpen(false);

    // Invalidate activity logs query to refresh the Recent Activity component
    queryClient.invalidateQueries({ queryKey: [QueryKeys.activityLogs] });

    // Refetch data to ensure we have the most up-to-date version
    refetch();
  };

  const handleFailedUpdate = (payload: unknown) => {
    console.error('Error updating media:', payload);
  };

  const handleThumbnailUpdate = (thumbnailUrl: string) => {
    if (mediaFile && thumbnailUrl) {
      try {
        // Track which fields are being changed
        const changedFields: string[] = [];

        // Check if thumbnail is actually changing
        if (mediaFile.metadata?.v_thumbnail !== thumbnailUrl) {
          changedFields.push('metadata.v_thumbnail');
        }

        // Add timestamp field
        changedFields.push('metadata.v_thumbnailTimestamp');

        // The thumbnail has already been updated by the API endpoint call
        // No need to call updateMediaMutation again which is causing duplicate logs

        // Close dialog
        setIsThumbnailDialogOpen(false);

        // Show success toast notification
        toast.success('Thumbnail updated successfully');

        // Invalidate activity logs query to refresh the Recent Activity component
        queryClient.invalidateQueries({ queryKey: [QueryKeys.activityLogs] });

        // Invalidate media queries to refresh the media library and media detail views
        queryClient.invalidateQueries({ queryKey: [QueryKeys.media] });
        queryClient.invalidateQueries({ queryKey: [QueryKeys.mediaDetail] });

        // Refetch to get latest data
        refetch();
        return Promise.resolve(true);
    } catch (error) {
        console.error('Error processing thumbnail update:', error);
        toast.error('Failed to update thumbnail');
        return Promise.resolve(false);
      }
    }
    return Promise.resolve(false);
  };

  // Convert mediaTypeInfo to MediaTypeConfig format
  const mediaTypeConfig = mediaFile && mediaTypes && mediaTypes.length > 0 ? (() => {
    const mtInfo = mediaTypes.find((type) => type.name === mediaFile.mediaType);
    return mtInfo ? { ...mtInfo, fields: mtInfo.fields || [] } : null;
  })() : null;

  // Get metadata description (from either root or metadata object)
  const description = mediaFile ? getMetadataField(mediaFile, 'description', '') : '';

  // Ensure modifiedDate exists - required by BaseMediaFile interface
  const modifiedDate = mediaFile ? (mediaFile.modifiedDate || new Date().toISOString()) : new Date().toISOString();

  // Get the media's accent color from its media type
  // const accentColor = mediaTypeInfo?.catColor || '#4dabf5'; // REMOVED - Logic moved to useEffect

  // Prepare data for MediaInformation component according to its expected props
  const baseFields = mediaFile ? [
    { name: 'File Name', value: getMetadataField(mediaFile, 'fileName', mediaFile.title) },
    { name: 'Media Type', value: mediaFile.mediaType },
    { name: 'File Size', value: formatFileSize(mediaFile.fileSize || 0) },
    { name: 'File Extension', value: mediaFile.fileExtension?.toUpperCase() },
    { name: 'Upload Date', value: new Date(modifiedDate).toLocaleDateString() },
    { name: 'Uploaded By', value: uploadedBy || userId || 'Unknown' },
    { name: 'Description', value: description },
    { name: 'Alt Text', value: getMetadataField(mediaFile, 'altText', '') },
    { name: 'Visibility', value: getMetadataField(mediaFile, 'visibility', 'private')?.toUpperCase() },
  ] : [];

  // Render the successful state with the media file
  return (
    <motion.div className="media-detail-container" {...motionProps}>
      <Button
        className="back-button"
        onClick={() => navigate("/media-library")}
        variant="outlined"
        size={isMobile ? "small" : "medium"}
      >
        <ArrowBackIcon fontSize={isMobile ? "small" : "medium"} />
      </Button>

      <Box
        className="media-detail"
        sx={{
          p: isMobile ? 2 : 3,
          maxWidth: '100%',
          margin: '0 auto'
        }}
      >
        <MediaDetailPreview 
          mediaFile={mediaFile as BaseMediaFile}
          onEdit={handleEdit}
          onDownload={handleDownload}
          isEditingEnabled={isEditingEnabled ?? false}
          onThumbnailUpdate={isVideo && isEditingEnabled ? () => setIsThumbnailDialogOpen(true) : undefined}
        />

        <Box className="media-detail-content">
          <Suspense fallback={<CircularProgress size={24} />}>
            <MediaInformation
              mediaFile={mediaFile}
              mediaTypeConfig={mediaTypeConfig}
              baseFields={baseFields}
              getMetadataField={getMetadataField}
            />
          </Suspense>
        </Box>
      </Box>

      {isEditDialogOpen && (
        <Suspense fallback={<CircularProgress size={24} />}>
          <EditMediaDialog
            open={isEditDialogOpen}
            onClose={() => setIsEditDialogOpen(false)}
            mediaFile={mediaFile as any}
            mediaType={mediaTypeConfig as any}
            onSave={async (data) => {
              await handleSave(data);
              return;
            }}
          />
        </Suspense>
      )}

      {isThumbnailDialogOpen && (
        <ThumbnailUpdateDialog
          open={isThumbnailDialogOpen}
          onClose={() => setIsThumbnailDialogOpen(false)}
          mediaData={mediaFile}
          onThumbnailUpdate={handleThumbnailUpdate}
        />
      )}
    </motion.div>
  );
};

export default MediaDetail;

import React, { useState, useEffect } from 'react';
import { useForm, Controller } from 'react-hook-form';
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Button,
  Chip,
  IconButton,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  FormControlLabel,
  Switch,
  Typography,
  Box,
  useMediaQuery,
  Theme,
  Alert,
  CircularProgress
} from '@mui/material';
import CloseIcon from '@mui/icons-material/Close';
import { MediaFile, MediaType } from '../../../types/media';
import '../styles/EditMediaDialog.scss';
import { useUserProfile } from '../../../hooks/query-hooks';
import { normalizeTag } from '../../../utils/mediaTypeUploaderUtils';
import { toast } from 'react-toastify';

// Define interface for MediaTypeField if it's missing from imports
interface MediaTypeField {
  name: string;
  type: string;
  required?: boolean;
  options?: string[];
  label?: string;
  [key: string]: any;
}

// Don't extend MediaFile if there are compatibility issues, define directly
interface ExtendedMediaFile {
  _id?: string;
  id?: string;
  title?: string;
  slug?: string;
  fileName?: string;
  altText?: string;
  description?: string;
  visibility?: 'public' | 'private';
  tags?: string[];
  customFields?: Record<string, any>;
  fileExtension?: string;
  fileSize?: number;
  mediaType?: string;
  location?: string;
  modifiedDate?: string;
  metadata?: {
    fileName?: string;
    altText?: string;
    description?: string;
    visibility?: 'public' | 'private';
    tags?: string[];
    [key: string]: any;
  };
}

// Helper function to compare arrays (for tags)
const compareArrays = (arr1: any[], arr2: any[]): boolean => {
  if (arr1.length !== arr2.length) return false;

  // Sort and normalize tags for comparison
  const sorted1 = [...arr1].map(tag => normalizeTag(tag)).sort();
  const sorted2 = [...arr2].map(tag => normalizeTag(tag)).sort();

  return sorted1.every((val, i) => val === sorted2[i]);
};

// Helper function to compare objects (for customFields)
const compareObjects = (obj1: Record<string, any>, obj2: Record<string, any>): boolean => {
  // Get non-empty keys from both objects
  const keys1 = Object.keys(obj1).filter(key => obj1[key] !== undefined && obj1[key] !== '');
  const keys2 = Object.keys(obj2).filter(key => obj2[key] !== undefined && obj2[key] !== '');

  // Log comparison details for debugging
  console.log('Comparing custom fields:', {
    obj1Count: keys1.length,
    obj2Count: keys2.length,
    keys1,
    keys2
  });

  // If key counts don't match, objects are different
  if (keys1.length !== keys2.length) {
    console.log('Key count mismatch, fields have changed');
    return false;
  }

  // Compare each field value 
  let allMatch = true;
  keys1.forEach(key => {
    // Skip special fields that are managed differently
    if (['thumbnailUrl'].includes(key)) return;

    const val1 = obj1[key];
    const val2 = obj2[key];

    // For non-primitive types (objects, arrays), use JSON comparison
    const isEqual = typeof val1 === 'object' || typeof val2 === 'object' 
      ? JSON.stringify(val1) === JSON.stringify(val2)
      : val1 === val2;

    if (!isEqual) {
      console.log(`Field "${key}" changed:`, {
        from: val2,
        to: val1,
        type1: typeof val1,
        type2: typeof val2
      });
      allMatch = false;
    }
  });

  return allMatch;
};

interface EditMediaDialogProps {
  open: boolean;
  onClose: () => void;
  mediaFile: ExtendedMediaFile;
  mediaType: MediaType;
  onSave: (data: Partial<MediaFile>) => Promise<void>;
}

interface FormValues {
  title: string;
  fileName: string;
  altText: string;
  description: string;
  visibility: 'public' | 'private';
  tags: string[];
  customFields: Record<string, any>;
}

export const EditMediaDialog: React.FC<EditMediaDialogProps> = ({
  open,
  onClose,
  mediaFile,
  mediaType,
  onSave
}) => {
  // Get user role from Redux
  const { data: userProfile } = useUserProfile();
  const userRole = userProfile?.role;
  const isSuperAdmin = userRole === 'superAdmin';
  const isMobile = useMediaQuery((theme: Theme) => theme.breakpoints.down('sm'));

  const [newTag, setNewTag] = useState('');
  // Track save in progress
  const [isSaving, setIsSaving] = useState(false);

  // State for forcing form rerendering
  const [formVersion, setFormVersion] = useState(0);

  // Add state to track original values for change detection
  const [originalValues, setOriginalValues] = useState<FormValues | null>(null);

  // Replace tagWarningOpen with a state to track unsaved tag warning
  const [unsavedTag, setUnsavedTag] = useState<string | null>(null);

  // Determine the source for custom fields for defaultValues
  let defaultCustomFieldsData = mediaFile.customFields;
  if (defaultCustomFieldsData === undefined && mediaFile.metadata && mediaType && mediaType.fields) {
    defaultCustomFieldsData = {};
    mediaType.fields.forEach((fieldDefinition: MediaTypeField) => {
      if (Object.prototype.hasOwnProperty.call(mediaFile.metadata!, fieldDefinition.name)) {
        defaultCustomFieldsData![fieldDefinition.name] = mediaFile.metadata![fieldDefinition.name];
      }
    });
  }
  defaultCustomFieldsData = defaultCustomFieldsData || {};

  const { control, handleSubmit, watch, setValue, reset, formState } = useForm<FormValues>({
    defaultValues: {
      title: mediaFile.title || '',
      fileName: mediaFile.metadata?.fileName || mediaFile.fileName || '',
      altText: mediaFile.metadata?.altText || mediaFile.altText || '',
      description: mediaFile.metadata?.description || mediaFile.description || '',
      visibility: (mediaFile.metadata?.visibility || mediaFile.visibility || 'private') as 'public' | 'private',
      tags: mediaFile.metadata?.tags || mediaFile.tags || [],
      customFields: defaultCustomFieldsData // Use derived custom fields
    }
  });

  // Reset form when the dialog opens or mediaFile changes
  useEffect(() => {
    if (open) {
      // console.log('Dialog opened, resetting form with fresh values');

      // Determine the source for custom fields for initialValues
      let sourceCustomFieldsForInitial = mediaFile.customFields;
      if (sourceCustomFieldsForInitial === undefined && mediaFile.metadata && mediaType && mediaType.fields) {
        sourceCustomFieldsForInitial = {};
        mediaType.fields.forEach((fieldDefinition: MediaTypeField) => {
          if (Object.prototype.hasOwnProperty.call(mediaFile.metadata!, fieldDefinition.name)) {
            sourceCustomFieldsForInitial![fieldDefinition.name] = mediaFile.metadata![fieldDefinition.name];
          }
        });
      }
      sourceCustomFieldsForInitial = sourceCustomFieldsForInitial || {};

      const initialValues = {
        title: mediaFile.title || '',
        fileName: mediaFile.metadata?.fileName || mediaFile.fileName || '',
        altText: mediaFile.metadata?.altText || mediaFile.altText || '',
        description: mediaFile.metadata?.description || mediaFile.description || '',
        visibility: (mediaFile.metadata?.visibility || mediaFile.visibility || 'private') as 'public' | 'private',
        tags: mediaFile.metadata?.tags || mediaFile.tags || [],
        customFields: sourceCustomFieldsForInitial // Use derived custom fields
      };

      // Store original values for change detection
      setOriginalValues(initialValues);

      // Force reset with current mediaFile values
      reset(initialValues);

      // Increment version to force re-render
      setFormVersion(prev => prev + 1);
    }
  }, [open, mediaFile, reset]);

  // Initialize form with default values and ensure default tags are included
  useEffect(() => {
    // When the dialog opens, re-initialize form values from mediaFile
    console.log('Dialog open state changed or mediaFile updated, refreshing form values');

    // Reset form values when dialog opens or mediaFile changes
    if (open) {
      // Make sure all default tags from the media type are included in the tags
      if (mediaType && mediaType.defaultTags && mediaType.defaultTags.length > 0) {
        const currentTags = watch('tags') || [];
        const defaultTags = mediaType.defaultTags;

        // Check if all default tags are included
        const allDefaultTagsIncluded = defaultTags.every((tag: string) => currentTags.includes(tag));

        if (!allDefaultTagsIncluded) {
          // Add any missing default tags
          const updatedTags = [...currentTags];
          defaultTags.forEach((tag: string) => {
            if (!updatedTags.includes(tag)) {
              updatedTags.push(tag);
            }
          });

          // Update the form
          setValue('tags', updatedTags);
        }
      }

      // Ensure all MediaType fields are properly loaded
      if (mediaType && mediaType.fields && mediaType.fields.length > 0) {
        console.log('Setting initial custom field values...');

        // Log all available customFields for debugging
        console.log('Available customFields:', mediaFile.customFields);

        // Check if mediaFile has metadata as well
        if ((mediaFile as any).metadata) {
          console.log('Available metadata from mediaFile:', (mediaFile as any).metadata);
        }

        mediaType.fields.forEach((field: MediaTypeField) => {
          // Try finding value in either customFields or metadata
          const fieldValueFromCustomFields = mediaFile.customFields?.[field.name];
          const fieldValueFromMetadata = (mediaFile as any).metadata?.[field.name];

          // Use the most appropriate value source
          const fieldValue = fieldValueFromCustomFields !== undefined 
            ? fieldValueFromCustomFields 
            : fieldValueFromMetadata;

          console.log(`Setting field "${field.name}" to:`, fieldValue, 
            `(from ${fieldValueFromCustomFields !== undefined ? 'customFields' : 'metadata'})`);

          // Set each field with appropriate type conversion
          if (field.type === 'boolean') {
            setValue(`customFields.${field.name}`, Boolean(fieldValue));
          } else if (field.type === 'number') {
            const numValue = fieldValue !== undefined && fieldValue !== null 
              ? Number(fieldValue) : '';
            setValue(`customFields.${field.name}`, numValue);
          } else {
            // For text, select, and other types
            setValue(`customFields.${field.name}`, fieldValue !== undefined ? fieldValue : '');
          }
        });
      }
    }
  }, [mediaType, setValue, watch, mediaFile.customFields, open, mediaFile]);

  // Reset the tag input field when the dialog opens or closes
  useEffect(() => {
    // Reset any tag-related state when dialog visibility changes
    setNewTag('');
    setUnsavedTag(null);
  }, [open]);

  const handleAddTag = (event: React.KeyboardEvent) => {
    if (event.key === 'Enter' && newTag.trim()) {
      // Normalize the tag
      const normalizedTag = normalizeTag(newTag.trim());

      // Check if user is a superAdmin for default tags
      const isDefaultTag = mediaType.defaultTags?.map((t: string) => normalizeTag(t)).includes(normalizedTag);

      // Only allow adding tags if not a default tag, or if user is a superAdmin
      if (isDefaultTag && !isSuperAdmin) {
        return;
      }

      const currentTags = watch('tags');
      // Check if normalized tag already exists (case-insensitive)
      if (!currentTags.map(t => normalizeTag(t)).includes(normalizedTag)) {
        setValue('tags', [...currentTags, normalizedTag]);
      }
      setNewTag('');
    }
  };

  const handleDeleteTag = (tagToDelete: string) => {
    const normalizedTagToDelete = normalizeTag(tagToDelete);

    // Check if the tag is a default tag (using normalized comparison)
    const isDefaultTag = mediaType.defaultTags?.some((tag: string) => 
      normalizeTag(tag) === normalizedTagToDelete
    );

    // Do not allow removing default tags from this UI
    if (isDefaultTag) {
      // For better UX, show a toast notification explaining why
      // If you have react-toastify or similar
      console.warn('Cannot remove default tag:', tagToDelete);
      return;
    }

    const currentTags = watch('tags');
    setValue('tags', currentTags.filter(tag => normalizeTag(tag) !== normalizedTagToDelete));
  };

  // Add debugging code to trace form value changes
  useEffect(() => {
    if (open && formState.isDirty) {
      const currentValues = watch();
      console.log('Form values changed:', currentValues);
      // If customFields changed, log the differences
      if (currentValues.customFields && originalValues?.customFields) {
        Object.keys(currentValues.customFields).forEach(key => {
          const newVal = currentValues.customFields[key];
          const oldVal = originalValues.customFields?.[key];
          if (newVal !== oldVal) {
            console.log(`Field "${key}" changed from:`, oldVal, 'to:', newVal);
          }
        });
      }
    }
  }, [watch, formState.isDirty, open, originalValues]);

  // Modified to debug custom fields on form submission
  const onSubmit = async (data: FormValues) => {
    // Check if there's an unpressed tag in the input
    if (newTag.trim()) {
      setUnsavedTag(newTag.trim());
      return; // Stop submission flow until user decides
    }
    // Call the main submission handler
    await handleFormSubmission(data);
  };

  // Add these handlers for the inline alert buttons
  const handleAddUnsavedTag = () => {
    if (unsavedTag) {
      const normalizedTag = normalizeTag(unsavedTag);
      const currentTags = watch('tags');

      // Check if normalized tag already exists (case-insensitive)
      if (!currentTags.map(t => normalizeTag(t)).includes(normalizedTag)) {
        // Add the tag
        const updatedTags = [...currentTags, normalizedTag];
        setValue('tags', updatedTags);

        // Proceed with form submission after adding tag
        const currentData = watch();
        currentData.tags = updatedTags;
        handleFormSubmission(currentData);
      }
    }
    setNewTag('');
    setUnsavedTag(null);
  };

  const handleIgnoreUnsavedTag = () => {
    // Skip adding tag and continue with form submission
    setUnsavedTag(null);
    setNewTag(''); // Clear the tag input
    handleFormSubmission(watch());
  };

  // Create a handler for when the dialog is closed
  const handleDialogClose = () => {
    // Clear any unsaved tag input when closing the dialog
    setNewTag('');
    setUnsavedTag(null);
    onClose();
  };

  // Extract the submission logic to a separate function to avoid duplication
  const handleFormSubmission = async (data: FormValues) => {
    setIsSaving(true);
    try {
      // Ensure all default tags are included
      if (mediaType && mediaType.defaultTags) {
        const finalTags = [...data.tags];
        let tagsChangedInternal = false; // Renamed to avoid conflict

        mediaType.defaultTags.forEach((tag: string) => {
          const normalizedDefaultTag = normalizeTag(tag);
          if (!finalTags.some(t => normalizeTag(t) === normalizedDefaultTag)) {
            finalTags.push(tag);
            tagsChangedInternal = true;
          }
        });

        if (tagsChangedInternal) {
          data.tags = finalTags;
        }
      }

      // Check if any values have actually changed before saving
      const customFieldsHaveChanged = !compareObjects(data.customFields, originalValues?.customFields || {});
      const standardFieldsHaveChanged = 
        data.title !== (originalValues?.title || '') ||
        data.fileName !== (originalValues?.fileName || '') ||
        data.altText !== (originalValues?.altText || '') ||
        data.description !== (originalValues?.description || '') ||
        data.visibility !== (originalValues?.visibility || '') || // Corrected: was missing originalValues
        !compareArrays(data.tags, originalValues?.tags || []);

      const overallHasChanged = standardFieldsHaveChanged || customFieldsHaveChanged;

      if (overallHasChanged) {
        const changedData: any = {
          _id: mediaFile._id || mediaFile.id,
          slug: (mediaFile as any).slug,
        };

        if (data.title !== (originalValues?.title || '')) {
          changedData.title = data.title;
        }

        changedData.metadata = {}; // Initialize metadata object

        if (data.fileName !== (originalValues?.fileName || '')) {
          changedData.metadata.fileName = data.fileName;
        }
        if (data.altText !== (originalValues?.altText || '')) {
          changedData.metadata.altText = data.altText;
        }
        if (data.description !== (originalValues?.description || '')) {
          changedData.metadata.description = data.description;
        }
        if (data.visibility !== (originalValues?.visibility || '')) { // Corrected
          changedData.metadata.visibility = data.visibility;
        }
        if (!compareArrays(data.tags, originalValues?.tags || [])) {
          changedData.metadata.tags = data.tags;
        }

        // Process custom fields correctly
        if (customFieldsHaveChanged) {
          const fullCustomFieldsPayload = { 
            ...(originalValues?.customFields || {}),
            ...data.customFields 
          };

          if (process.env.NODE_ENV === 'development') {
            console.log("DEBUG: EditMediaDialog - Merged customFields for payload:", JSON.stringify(fullCustomFieldsPayload, null, 2));
          }

          Object.keys(fullCustomFieldsPayload).forEach(key => {
            if (mediaType.fields?.some(field => field.name === key)) {
              changedData.metadata[key] = fullCustomFieldsPayload[key];
            }
          });
        }

        if (Object.keys(changedData.metadata).length === 0) {
          delete changedData.metadata; // Remove if empty
        }

        if (process.env.NODE_ENV === 'development') {
          console.log('DEBUG: EditMediaDialog - Submitting changedData:', JSON.stringify(changedData, null, 2));
        }
        await onSave(changedData);

      } else {
        if (process.env.NODE_ENV === 'development') {
          console.log('DEBUG: EditMediaDialog - No changes detected. Skipping save operation.');
        }
      }

      handleDialogClose();
    } catch (error) {
      console.error('Error saving media:', error);
      toast.error('An error occurred while saving. Please try again.');
    } finally {
      setIsSaving(false);
    }
  };

  const renderCustomField = (field: MediaType['fields'][0], value: any, onChange: (value: any) => void) => {
    // Use field.label if available, otherwise use field.name
    const displayLabel = field.label || field.name;

    // Ensure the value is properly formatted based on field type
    let formattedValue = value;

    // Debug the field value
    console.log(`Formatting field "${field.name}" of type "${field.type}" with value:`, value);

    switch (field.type) {
      case 'text':
        formattedValue = value || '';
        return (
          <TextField
            label={displayLabel}
            value={formattedValue}
            onChange={(e) => onChange(e.target.value)}
            fullWidth
            size="small"
            required={field.required}
          />
        );
      case 'number':
        // Ensure we have a numeric value or empty string
        formattedValue = value !== undefined && value !== null ? Number(value) : '';
        return (
          <TextField
            label={displayLabel}
            value={formattedValue}
            onChange={(e) => {
              const numericValue = e.target.value === '' ? '' : Number(e.target.value);
              onChange(numericValue);
            }}
            type="number"
            fullWidth
            size="small"
            required={field.required}
          />
        );
      case 'select':
        // Ensure we have a valid selection
        formattedValue = value || '';
        return (
          <FormControl fullWidth size="small" required={field.required}>
            <InputLabel>{displayLabel}</InputLabel>
            <Select
              value={formattedValue}
              onChange={(e) => onChange(e.target.value)}
              label={displayLabel}
            >
              {field.options?.map((option: string) => (
                <MenuItem key={option} value={option}>
                  {option}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
        );
      case 'boolean':
        // Ensure we have a boolean value
        formattedValue = Boolean(value);
        return (
          <FormControlLabel
            control={
              <Switch
                checked={formattedValue}
                onChange={(e) => onChange(e.target.checked)}
              />
            }
            label={displayLabel}
          />
        );
      default:
        // For any other type, use a text field
        formattedValue = value || '';
        return (
          <TextField
            label={displayLabel}
            value={formattedValue}
            onChange={(e) => onChange(e.target.value)}
            fullWidth
            size="small"
          />
        );
    }
  };

  return (
    <Dialog
      key={`media-edit-${mediaFile.id}-${open}`}
      open={open}
      onClose={handleDialogClose}
      className="edit-media-dialog"
      maxWidth="md"
      fullWidth
      fullScreen={isMobile}
    >
      <DialogTitle className="dialog-title">
        Edit Media Details
        <IconButton
          onClick={handleDialogClose}
          sx={{ position: 'absolute', right: 8, top: 8 }}
          size={isMobile ? "small" : "medium"}
        >
          <CloseIcon fontSize={isMobile ? "small" : "medium"} />
        </IconButton>
      </DialogTitle>

      <DialogContent className="dialog-content">
        <form 
          key={`form-${mediaFile.id}-${formVersion}`}
          onSubmit={handleSubmit(onSubmit)} 
          className="edit-form"
        >
          {/* Unsaved tag warning alert */}
          {unsavedTag && (
            <Alert 
              severity="warning" 
              sx={{ mb: 2 }}
              action={
                <Box sx={{ display: 'flex', alignItems: 'center' }}>
                  <Button 
                    color="primary" 
                    size="small" 
                    onClick={handleAddUnsavedTag}
                    sx={{ mr: 1 }}
                  >
                    Add Tag
                  </Button>
                  <Button 
                    color="inherit" 
                    size="small" 
                    onClick={handleIgnoreUnsavedTag}
                  >
                    Ignore
                  </Button>
                </Box>
              }
            >
              You have an unsaved tag: <strong>"{unsavedTag}"</strong>
            </Alert>
          )}

          {/* Basic Information Section (CSS grid) */}
          <Box className="form-section" sx={{ marginTop: isMobile ? 2 : 4, padding: 2, marginBottom: 0 }}>
            <Typography variant={isMobile ? "subtitle1" : "h6"} gutterBottom>Basic Information</Typography>
            <Box sx={{
              display: 'grid',
              gap: isMobile ? 2 : 4,
              gridTemplateColumns: isMobile ? '1fr' : '1fr 1fr'
            }}>
              <Box>
                <Controller
                  name="title"
                  control={control}
                  render={({ field }) => (
                    <TextField
                      {...field}
                      label="Title"
                      fullWidth
                      size="small"
                    />
                  )}
                />
              </Box>
              <Box>
                <Controller
                  name="fileName"
                  control={control}
                  render={({ field }) => (
                    <TextField
                      {...field}
                      label="File Name"
                      fullWidth
                      size="small"
                    />
                  )}
                />
              </Box>
              <Box>
                <Controller
                  name="altText"
                  control={control}
                  render={({ field }) => (
                    <TextField
                      {...field}
                      label="Alt Text"
                      fullWidth
                      size="small"
                    />
                  )}
                />
              </Box>
              <Box>
                <Controller
                  name="visibility"
                  control={control}
                  render={({ field }) => (
                    <FormControl fullWidth size="small">
                      <InputLabel>Visibility</InputLabel>
                      <Select {...field} label="Visibility">
                        <MenuItem value="public">Public</MenuItem>
                        <MenuItem value="private">Private</MenuItem>
                      </Select>
                    </FormControl>
                  )}
                />
              </Box>
              <Box sx={{ gridColumn: '1 / -1' }}>
                <Controller
                  name="description"
                  control={control}
                  render={({ field }) => (
                    <TextField
                      {...field}
                      label="Description"
                      fullWidth
                      multiline
                      rows={isMobile ? 3 : 2}
                      size="small"
                    />
                  )}
                />
              </Box>
            </Box>
          </Box>

          {/* Custom Fields Section */}
          {mediaType.fields.length > 0 && (
            <Box className="form-section" sx={{ marginTop: isMobile ? 2 : 4, padding: 2 }}>
              <Typography variant={isMobile ? "subtitle1" : "h6"} gutterBottom>{mediaType.name} Fields</Typography>
              <Box sx={{ display: 'grid', gap: isMobile ? 2 : 4, gridTemplateColumns: isMobile ? '1fr' : '1fr 1fr' }}>
                {mediaType.fields.map((field) => {
                  // Log the current field definition and its value
                  console.log(`Rendering field "${field.name}" (${field.type}):`, 
                    mediaFile.customFields?.[field.name]);

                  return (
                    <Box key={field.name}>
                      <Controller
                        name={`customFields.${field.name}`}
                        control={control}
                        defaultValue={mediaFile.customFields?.[field.name] || ''}
                        render={({ field: { value, onChange } }) => {
                          // Log each value change 
                          const handleChange = (newValue: any) => {
                            console.log(`Field "${field.name}" changed to:`, newValue);
                            onChange(newValue);
                          };
                          return renderCustomField(field, value, handleChange);
                        }}
                      />
                    </Box>
                  );
                })}
              </Box>
            </Box>
          )}

          <Box className="form-section" sx={{ marginTop: isMobile ? 2 : 4, padding: 2 }}>
            <Typography variant={isMobile ? "subtitle1" : "h6"} gutterBottom>Tags</Typography>
            <Typography variant="caption" color="text.secondary" sx={{ mb: isMobile ? 1 : 2, display: 'block', fontSize: isMobile ? '0.7rem' : '0.75rem' }}>
              Default tags from the media type cannot be removed here. They can only be modified by superAdmins in the Media Types settings.
            </Typography>
            <Box sx={{ display: 'grid', gap: isMobile ? 2 : 4 }}>
              <Box>
                <TextField
                  label="Add Tags"
                  value={newTag}
                  onChange={(e) => setNewTag(e.target.value)}
                  onKeyDown={handleAddTag}
                  fullWidth
                  size="small"
                  helperText="Press Enter to add a tag"
                />
                <Box sx={{ mt: 1, display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
                  {watch('tags').map((tag) => {
                    const isDefaultTag = mediaType.defaultTags?.includes(tag);
                    return (
                      <Chip
                        key={tag}
                        label={tag}
                        onDelete={isDefaultTag ? undefined : () => handleDeleteTag(tag)}
                        size="small"
                        color={isDefaultTag ? "primary" : "default"}
                        sx={isDefaultTag ? {
                          borderWidth: isMobile ? '1px' : '2px',
                          borderStyle: 'solid',
                          borderColor: 'var(--secondary-color)',
                          position: 'relative',
                        } : {}}
                        title={isDefaultTag ? "This is a default tag from the media type and cannot be removed" : "Click 'x' to remove this tag"}
                      />
                    );
                  })}
                </Box>
              </Box>
            </Box>
          </Box>
        </form>
      </DialogContent>

      <DialogActions className="dialog-actions">
        <Button onClick={handleDialogClose} size={isMobile ? "small" : "medium"}>Cancel</Button>
        <Button
          onClick={handleSubmit(onSubmit)}
          variant="contained"
          color="primary"
          size={isMobile ? "small" : "medium"}
          disabled={isSaving}
        >
          {isSaving ? <CircularProgress size={20} /> : 'Save Changes'}
        </Button>
      </DialogActions>
    </Dialog>
  );
};

