import CircleIcon from "@mui/icons-material/Circle";
import FieldLabel from "./FieldLabel";
import FieldInput from "./FieldInput";
import { MediaType, TagCategory } from "../../../hooks/query-hooks";
import { MetadataState } from "../types";
import StandardFields from "./StandardFields";
import { SelectChangeEvent } from "@mui/material";
import "./FormFieldsTab.css";

interface FormFieldsTabProps {
  currentTab: number;
  setCurrentTab: (index: number) => void;
  matchingType: MediaType;
  baseType: string;
  baseFields: Record<string, any>;
  metadata: MetadataState;
  handleMetadataChange: (field: string, value: any) => void;
  tagCategories: TagCategory[];
  hasIncompleteStandardFields: boolean;
  hasIncompleteCustomFields: boolean;
  hasIncompleteBaseFields: boolean;
  isStandardTabComplete: boolean;
  isCustomTabComplete: boolean;
  isBaseTabComplete: boolean;
  showTagFilter: boolean;
  setShowTagFilter: React.Dispatch<React.SetStateAction<boolean>>;
  getAvailableTags: () => string[];
  handleTagsChange: (event: React.ChangeEvent<HTMLInputElement>) => void;
  handleTagsBlur: () => void;
  handleTagsKeyDown: (event: React.KeyboardEvent<HTMLInputElement>) => void;
  handleTagCategoryChange: (event: SelectChangeEvent) => void;
  handleTagSearch: (event: React.ChangeEvent<HTMLInputElement>) => void;
  file: File | null;
  isCustomSection?: boolean;
  mediaType?: MediaType | null;
}

interface TabPanelProps {
  children?: React.ReactNode;
  index: number;
  value: number;
}

const TabPanel = (props: TabPanelProps) => {
  const { children, value, index, ...other } = props;
  const isActive = value === index;

  return (
    <Box
      role="tabpanel"
      hidden={!isActive}
      id={`media-uploader-tabpanel-${index}`}
      aria-labelledby={`media-uploader-tab-${index}`}
      className={`tab-panel ${isActive ? 'tab-panel-active' : 'tab-panel-inactive'}`}
      {...other}
    >
      {isActive && (
        <Box className="tab-panel-content">
          {children}
        </Box>
      )}
    </Box>
  );
};

const a11yProps = (index: number) => {
  return {
    id: `media-uploader-tab-${index}`,
    'aria-controls': `media-uploader-tabpanel-${index}`,
  };
};

const FormFieldsTab: React.FC<FormFieldsTabProps> = ({
  currentTab,
  setCurrentTab,
  matchingType,
  baseType,
  baseFields,
  metadata,
  handleMetadataChange,
  tagCategories,
  hasIncompleteStandardFields,
  hasIncompleteCustomFields,
  hasIncompleteBaseFields,
  isStandardTabComplete,
  isCustomTabComplete,
  isBaseTabComplete,
  showTagFilter,
  setShowTagFilter,
  getAvailableTags,
  handleTagsChange,
  handleTagsBlur,
  handleTagsKeyDown,
  handleTagCategoryChange,
  handleTagSearch,
  file,
  isCustomSection = false,
  mediaType = null
}) => {
  const handleChange = (_event: React.SyntheticEvent, newValue: number) => {
    setCurrentTab(newValue);
  };

  return (
    <Box className="fields-container">
      <Box className="tabs-container">
        <Tabs 
          value={currentTab} 
          onChange={handleChange} 
          variant="fullWidth"
          aria-label="form tabs"
        >
          <Tab 
            label={
              <Box className="tab-label">
                <Typography>Standard Information</Typography>
                <Box className="tab-indicator">
                  {isStandardTabComplete ? (
                    <DoneIcon className="complete-icon" />
                  ) : (
                    <CircleIcon 
                      className={hasIncompleteStandardFields ? "incomplete-icon" : "neutral-icon"}
                    />
                  )}
                </Box>
              </Box>
            } 
            {...a11yProps(0)}
          />

          {(matchingType.fields || []).length > 0 && (
            <Tab 
              label={
                <Box className="tab-label">
                  <Typography>{matchingType.name} Fields</Typography>
                  <Box className="tab-indicator">
                    {isCustomTabComplete ? (
                      <DoneIcon className="complete-icon" />
                    ) : (
                      <CircleIcon 
                        className={hasIncompleteCustomFields ? "incomplete-icon" : "neutral-icon"}
                      />
                    )}
                  </Box>
                </Box>
              } 
              {...a11yProps(1)}
            />
          )}

          {Object.keys(baseFields).length > 0 && (
            <Tab 
              label={
                <Box className="tab-label">
                  <Typography>{baseType.replace('Base', '')} Properties</Typography>
                  <Box className="tab-indicator">
                    {isBaseTabComplete ? (
                      <DoneIcon className="complete-icon" />
                    ) : (
                      <CircleIcon 
                        className={hasIncompleteBaseFields ? "incomplete-icon" : "neutral-icon"}
                      />
                    )}
                  </Box>
                </Box>
              } 
              {...a11yProps(2)}
            />
          )}
        </Tabs>

        <Box className="tab-panels-container">
        <TabPanel value={currentTab} index={0}>
          {!isCustomSection ? (
            <Paper className="tab-content-paper" elevation={0} variant="outlined">
              <StandardFields 
                metadata={metadata}
                handleMetadataChange={handleMetadataChange}
                showTagFilter={showTagFilter}
                setShowTagFilter={setShowTagFilter}
                tagCategories={tagCategories}
                getAvailableTags={getAvailableTags}
                handleTagsChange={handleTagsChange}
                handleTagsBlur={handleTagsBlur}
                handleTagsKeyDown={handleTagsKeyDown}
                handleTagCategoryChange={handleTagCategoryChange}
                handleTagSearch={handleTagSearch}
              />
            </Paper>
          ) : (
            <Paper className="tab-content-paper" elevation={0} variant="outlined">
              {mediaType && mediaType.fields && mediaType.fields.length > 0 ? (
                <Box className="fields-grid">
                  {mediaType.fields.map((field: any, index) => (
                    <Box 
                      className={field.type === 'TextArea' ? 'field-full-width' : 'field-half-width'}
                      key={`${field.name}-${index}`}
                    >
                      <FieldLabel 
                        name={field.name} 
                        required={field.required} 
                        isValid={field.required ? (metadata[field.name] ? true : false) : null}
                      />
                      <FieldInput
                        field={field}
                        keyName={field.name}
                        metadata={metadata}
                        tagCategories={tagCategories}
                        handleMetadataChange={handleMetadataChange}
                      />
                    </Box>
                  ))}
                </Box>
              ) : (
                <Typography variant="body1" className="empty-message" align="center">
                  No custom fields for this media type.
                </Typography>
              )}
            </Paper>
          )}
        </TabPanel>

        <TabPanel value={currentTab} index={1}>
          {(matchingType.fields || []).length > 0 && (
            <Paper className="tab-content-paper" elevation={0} variant="outlined">
              <Box className="fields-grid">
                {(matchingType.fields || []).map((field: any, index) => (
                  <Box 
                    className={field.type === 'TextArea' ? 'field-full-width' : 'field-half-width'}
                    key={`${field.name}-${index}`}
                  >
                    <FieldLabel 
                      name={field.name} 
                      required={field.required} 
                      isValid={field.required ? (metadata[field.name] ? true : false) : null}
                    />
                    <FieldInput
                      field={field}
                      keyName={field.name}
                      metadata={metadata}
                      tagCategories={tagCategories}
                      handleMetadataChange={handleMetadataChange}
                    />
                  </Box>
                ))}
              </Box>
            </Paper>
          )}
        </TabPanel>

        <TabPanel value={currentTab} index={2}>
          {Object.keys(baseFields).length > 0 ? (
            <Paper className="tab-content-paper" elevation={0} variant="outlined">
              <Box className="fields-grid">
                {Object.entries(baseFields).map(([key, field]) => {
                  // Skip fields that are already populated automatically
                  if ((key === 'imageWidth' || key === 'imageHeight') && 
                      metadata[key] !== undefined) {
                    return (
                      <Box className="field-half-width" key={key}>
                        <FieldLabel 
                          name={key} 
                          required={false} 
                          isValid={true}
                        />
                        <FieldInput
                          field={{ 
                            type: 'text', 
                            required: false,
                            name: key
                          }}
                          keyName={key}
                          metadata={{...metadata, [key]: metadata[key] || "Auto-detected"}}
                          tagCategories={tagCategories}
                          handleMetadataChange={() => {}}
                        />
                      </Box>
                    );
                  }

                  // Use type assertion to solve the 'field is of type unknown' error
                  const fieldWithType = field as {
                    type: string;
                    options?: string[];
                    required?: boolean;
                    name?: string;
                    useTagCategory?: boolean;
                    tagCategoryId?: string;
                  };

                  const isRequired = !!fieldWithType.required;
                  const isEmpty = metadata[key] === undefined || metadata[key] === "";
                  const isValid = isRequired ? !isEmpty : null;

                  // Render appropriate input based on field type
                  return (
                    <Box className="field-half-width" key={key}>
                      <FieldLabel 
                        name={key} 
                        required={isRequired} 
                        isValid={isValid}
                      />
                      <FieldInput
                        field={fieldWithType}
                        keyName={key}
                        metadata={metadata}
                        tagCategories={tagCategories}
                        handleMetadataChange={handleMetadataChange}
                      />
                    </Box>
                  );
                })}
              </Box>
            </Paper>
          ) : (
            <Paper className="empty-tab-content-paper" elevation={0} variant="outlined">
              <Typography variant="body1" className="empty-message">
                No {baseType.replace('Base', '')} specific properties found for this file.
              </Typography>
              <Typography variant="body2" color="text.secondary">
                {baseType === 'Media' 
                  ? "The selected media type doesn't have a specific base type defined."
                  : `Base properties would appear here when you upload a ${baseType.replace('Base', '').toLowerCase()} file.`}
              </Typography>
              <Box className="file-info-box">
                <Typography variant="caption" display="block" align="left">
                  Current file type: {file ? file.type : 'No file selected'}
                </Typography>
                <Typography variant="caption" display="block" align="left">
                  Media type base: {baseType}
                </Typography>
                <Typography variant="caption" display="block" align="left">
                  Expected MIME type: {baseType === 'BaseImage' ? 'image/*' : 
                    baseType === 'BaseVideo' ? 'video/*' : 
                    baseType === 'BaseAudio' ? 'audio/*' : 
                    baseType === 'BaseDocument' ? 'application/pdf' : 'any'}
                </Typography>
              </Box>
            </Paper>
          )}
        </TabPanel>
      </Box>

      </Box>


    </Box>
  );
};

export default FormFieldsTab;

import React, { useEffect } from 'react';
import {
  Box, 
  Typography,
  Tabs,
  Tab,
  SelectChangeEvent
} from '@mui/material';
import { MetadataState } from '../types';
import SimpleFormFieldsTab from "./SimpleFormFieldsTab";
import RelatedMediaSection from "./RelatedMediaSection";
import { MediaType, TagCategory } from "../../../hooks/query-hooks";
import { 
  hasIncompleteStandardFields, 
  hasIncompleteCustomFields, 
  findMediaType,
  supportsRelatedMedia as checkRelatedMediaSupport
} from "../utils";

interface MetadataFormProps {
  mediaTypes: MediaType[];
  selectedMediaType: string;
  file: File | null;
  metadata: MetadataState;
  handleMetadataChange: (field: string, value: any) => void;
  tagCategories: TagCategory[];
  showTagFilter: boolean;
  setShowTagFilter: React.Dispatch<React.SetStateAction<boolean>>;
  getAvailableTags: () => string[];
  handleTagsChange: (event: React.ChangeEvent<HTMLInputElement>) => void;
  handleTagsBlur: () => void;
  handleTagsKeyDown: (event: React.KeyboardEvent<HTMLInputElement>) => void;
  handleTagCategoryChange: (event: SelectChangeEvent) => void;
  handleTagSearch: (event: React.ChangeEvent<HTMLInputElement>) => void;
  currentTab: number;
  setCurrentTab: React.Dispatch<React.SetStateAction<number>>;
}

const MetadataForm: React.FC<MetadataFormProps> = ({
  mediaTypes,
  selectedMediaType,
  file,
  metadata,
  handleMetadataChange,
  tagCategories,
  showTagFilter,
  setShowTagFilter,
  getAvailableTags,
  handleTagsChange,
  handleTagsBlur,
  handleTagsKeyDown,
  handleTagCategoryChange,
  handleTagSearch,
  currentTab,
  setCurrentTab
}) => {

  // Find the selected media type
  const currentMediaType = findMediaType(mediaTypes, selectedMediaType);

  // Validate media fields
  const hasStandardErrors = hasIncompleteStandardFields(metadata);
  const hasCustomErrors = hasIncompleteCustomFields(metadata, currentMediaType);

  // Check if the current media type supports related media
  const supportsRelatedMedia = checkRelatedMediaSupport(currentMediaType);

  // Determine tab labels and status indicators
  const tabLabels = {
    0: hasStandardErrors ? "Basic Info " : "Basic Info",
    1: hasCustomErrors ? "Media Fields " : "Media Fields",
    2: "Related Media" 
  };

  // Handle tab change
  const handleTabChange = (_event: React.SyntheticEvent, newValue: number) => {
    // Don't let users navigate to related media tab if it's not supported
    if (newValue === 2 && !supportsRelatedMedia) {
      return;
    }
    setCurrentTab(newValue);
  };

  // Reset to first tab if currently on related media tab but type changed to one that doesn't support it
  useEffect(() => {
    if (currentTab === 2 && !supportsRelatedMedia) {
      setCurrentTab(0);
    }
  }, [currentTab, supportsRelatedMedia, setCurrentTab]);

  return (
    <Box sx={{ width: "100%", height: "100%", display: "flex", flexDirection: "column" }}>
      <Typography variant="h6" sx={{ mb: 2 }}>
        Add Metadata for {file?.name}
      </Typography>

      <Box sx={{ borderBottom: 1, borderColor: "divider", mb: 2 }}>
        <Tabs
          value={currentTab}
          onChange={handleTabChange}
          aria-label="metadata tabs"
          variant="fullWidth"
          sx={{ mb: 1 }}
        >
          <Tab label={tabLabels[0]} />
          <Tab label={tabLabels[1]} />
          {supportsRelatedMedia && <Tab label={tabLabels[2]} />}
        </Tabs>
      </Box>

      <Box sx={{ flexGrow: 1, overflow: "auto", pb: 2 }}>
        {currentTab === 0 && (
          <SimpleFormFieldsTab
            metadata={metadata}
            handleMetadataChange={handleMetadataChange}
            tagCategories={tagCategories}
            showTagFilter={showTagFilter}
            setShowTagFilter={setShowTagFilter}
            getAvailableTags={getAvailableTags}
            handleTagsChange={handleTagsChange}
            handleTagsBlur={handleTagsBlur}
            handleTagsKeyDown={handleTagsKeyDown}
            handleTagCategoryChange={handleTagCategoryChange}
            handleTagSearch={handleTagSearch}
            isCustomSection={false}
          />
        )}

        {currentTab === 1 && (
          <SimpleFormFieldsTab
            metadata={metadata}
            handleMetadataChange={handleMetadataChange}
            mediaType={currentMediaType}
            tagCategories={tagCategories}
            isCustomSection={true}
          />
        )}

        {currentTab === 2 && supportsRelatedMedia && (
          <RelatedMediaSection
            metadata={metadata}
            handleMetadataChange={handleMetadataChange}
          />
        )}
      </Box>
    </Box>
  );
};

export default MetadataForm;

import React, { useState } from 'react';
import {
  Box, 
  Typography,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  TextField,
  Button,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  IconButton
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import AddIcon from '@mui/icons-material/Add';
import { MetadataState } from '../types';
import MediaPicker from '../../MediaPicker/MediaPicker';
import { BaseMediaFile } from '../../../interfaces/MediaFile';

interface RelatedMediaSectionProps {
  metadata: MetadataState;
  handleMetadataChange: (field: string, value: any) => void;
}

const RelatedMediaSection: React.FC<RelatedMediaSectionProps> = ({ 
  metadata, 
  handleMetadataChange 
}) => {
  const [isPickerOpen, setIsPickerOpen] = useState(false);
  const [relationshipType, setRelationshipType] = useState('reference');
  const [note, setNote] = useState('');
  const relatedMedia = metadata.relatedMedia || [];

  // Handle selecting a media item from the picker
  const handleMediaSelect = (media: BaseMediaFile) => {
    const mediaId = media._id || media.id || '';
    if (!mediaId) return;

    // Check if this media is already related
    const alreadyExists = relatedMedia.some(item => item.mediaId === mediaId);
    if (alreadyExists) return;

    // Create the new related media item
    const newRelatedMedia = [
      ...relatedMedia,
      {
        mediaId,
        relationship: relationshipType,
        note: note.trim() || undefined,
        // Add display info for the UI (these won't be saved in the actual metadata)
        _display: {
          title: media.metadata?.fileName || media.title || 'Untitled',
          thumbnail: media.metadata?.v_thumbnail || media.location
        }
      }
    ];

    // Update the metadata
    handleMetadataChange('relatedMedia', newRelatedMedia);

    // Reset the form fields
    setRelationshipType('reference');
    setNote('');
  };

  // Handle removing a related media item
  const handleRemoveRelatedMedia = (index: number) => {
    const newRelatedMedia = [...relatedMedia];
    newRelatedMedia.splice(index, 1);
    handleMetadataChange('relatedMedia', newRelatedMedia);
  };

  return (
    <Box sx={{ mt: 2 }}>
      <Typography variant="subtitle1" gutterBottom>
        Related Media
      </Typography>

      <Box sx={{ display: 'flex', gap: 1, alignItems: 'flex-start', mb: 2 }}>
        <FormControl sx={{ minWidth: 150 }}>
          <InputLabel id="relationship-type-label">Relationship</InputLabel>
          <Select
            labelId="relationship-type-label"
            value={relationshipType}
            label="Relationship"
            size="small"
            onChange={(e) => setRelationshipType(e.target.value)}
          >
            <MenuItem value="reference">Reference</MenuItem>
            <MenuItem value="version">Version</MenuItem>
            <MenuItem value="attachment">Attachment</MenuItem>
            <MenuItem value="parent">Parent</MenuItem>
            <MenuItem value="child">Child</MenuItem>
          </Select>
        </FormControl>

        <TextField
          label="Note (optional)"
          value={note}
          onChange={(e) => setNote(e.target.value)}
          size="small"
          sx={{ flexGrow: 1 }}
        />

        <Button 
          variant="outlined" 
          startIcon={<AddIcon />}
          onClick={() => setIsPickerOpen(true)}
        >
          Add Media
        </Button>
      </Box>

      {/* List of related media */}
      {relatedMedia.length > 0 ? (
        <List dense sx={{ bgcolor: 'background.paper', borderRadius: 1, mb: 2 }}>
          {relatedMedia.map((item, index) => (
            <ListItem key={item.mediaId} divider={index < relatedMedia.length - 1}>
              <ListItemText
                primary={item._display?.title || `Media ID: ${item.mediaId}`}
                secondary={
                  <>
                    <Typography component="span" variant="body2" color="text.secondary">
                      {item.relationship}
                    </Typography>
                    {item.note && (
                      <Typography component="span" variant="body2" color="text.secondary" sx={{ ml: 1 }}>
                        - {item.note}
                      </Typography>
                    )}
                  </>
                }
              />
              <ListItemSecondaryAction>
                <IconButton 
                  edge="end" 
                  aria-label="delete"
                  onClick={() => handleRemoveRelatedMedia(index)}
                >
                  <DeleteIcon />
                </IconButton>
              </ListItemSecondaryAction>
            </ListItem>
          ))}
        </List>
      ) : (
        <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
          No related media added yet.
        </Typography>
      )}

      {/* Media picker */}
      <MediaPicker
        open={isPickerOpen}
        onClose={() => setIsPickerOpen(false)}
        onSelect={handleMediaSelect}
        title="Select Related Media"
        excludeIds={[
          // Exclude the current media item (if it has an ID)
          ...(metadata.mediaId ? [metadata.mediaId] : []),
          // Also exclude already related media
          ...relatedMedia.map(item => item.mediaId)
        ]}
      />
    </Box>
  );
};

