interface TagCategoryFormData {
  name: string;
  description: string;
  tags: Array<{ id: string; name: string }>;
}

interface TagCategoryFormProps {
  open: boolean;
  formData: TagCategoryFormData;
  setFormData: React.Dispatch<React.SetStateAction<TagCategoryFormData>>;
  onClose: () => void;
  onSubmit: () => void;
  isEditing: boolean;
}

export const TagCategoryForm: React.FC<TagCategoryFormProps> = ({
  open,
  formData,
  setFormData,
  onClose,
  onSubmit,
  isEditing
}) => {
  // Use TanStack Query hooks instead of Redux
  const { data: userProfile } = useUserProfile();
  const { data: tags = [] } = useTags(userProfile);
  const { mutateAsync: createTag } = useCreateTag();
  const { mutateAsync: createTagCategory } = useCreateTagCategory();
  const { mutateAsync: updateTagCategory } = useUpdateTagCategory();

  const [searchTerm, setSearchTerm] = useState('');
  const [newTagName, setNewTagName] = useState('');
  const [showCreateTag, setShowCreateTag] = useState(false);

  // Filter tags based on search term
  const filteredTags = tags.filter(tag => 
    tag.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Check if search term doesn't match any existing tags
  useEffect(() => {
    const tagExists = tags.some(tag => 
      tag.name.toLowerCase() === searchTerm.toLowerCase()
    );

    if (searchTerm && !tagExists && filteredTags.length === 0) {
      setShowCreateTag(true);
      setNewTagName(searchTerm);
    } else {
      setShowCreateTag(false);
      setNewTagName('');
    }
  }, [searchTerm, tags, filteredTags]);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSearchChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSearchTerm(e.target.value);
  };

  const handleTagToggle = (tagId: string, tagName: string) => {
    setFormData(prev => {
      // Check if the tag is already in the tags array
      const tagIndex = prev.tags.findIndex(tag => tag.id === tagId);

      if (tagIndex !== -1) {
        // Remove tag if it exists
        return { 
          ...prev, 
          tags: prev.tags.filter(tag => tag.id !== tagId)
        };
      } else {
        // Add tag
        return { 
          ...prev, 
          tags: [...prev.tags, { id: tagId, name: tagName }]
        };
      }
    });
  };

  const handleCreateNewTag = async () => {
    if (newTagName.trim()) {
      try {
        // Use TanStack Query mutation instead of Redux action
        const newTag = await createTag(newTagName.trim());

        // Add the newly created tag to the selected tags
        if (newTag && newTag._id) {
          handleTagToggle(newTag._id, newTag.name);
        }

        // Reset states
        setNewTagName('');
        setSearchTerm('');
        setShowCreateTag(false);
      } catch (error) {
        console.error('Failed to create new tag:', error);
      }
    }
  };

  const handleOnDragEnd = (result: DropResult) => {
    if (!result.destination) return;

    if (result.source.droppableId === 'availableTags' && 
        result.destination.droppableId === 'selectedTags') {
      // User dragged from available to selected
      const tagId = result.draggableId;
      const tag = tags.find(t => t._id === tagId);
      if (tag && !formData.tags.some(t => t.id === tagId)) {
        handleTagToggle(tagId, tag.name);
      }
    } else if (result.source.droppableId === 'selectedTags' && 
               result.destination.droppableId === 'availableTags') {
      // User dragged from selected to available (remove)
      const tagId = result.draggableId;
      if (formData.tags.some(t => t.id === tagId)) {
        handleTagToggle(tagId, '');
      }
    }
  };

  return (
    <Dialog open={open} onClose={onClose} maxWidth="md" fullWidth>
      <DialogTitle>
        {isEditing ? 'Edit Tag Category' : 'Create Tag Category'}
      </DialogTitle>
      <DialogContent>
        <TextField
          autoFocus
          margin="dense"
          name="name"
          label="Category Name"
          type="text"
          fullWidth
          variant="outlined"
          value={formData.name}
          onChange={handleInputChange}
          required
          sx={{ mb: 2 }}
        />
        <TextField
          margin="dense"
          name="description"
          label="Description (optional)"
          type="text"
          fullWidth
          variant="outlined"
          value={formData.description}
          onChange={handleInputChange}
          multiline
          rows={2}
          sx={{ mb: 3 }}
        />

        <Typography variant="subtitle2" sx={{ mb: 1 }}>
          Select Tags for this Category
        </Typography>

        {tags.length === 0 ? (
          <Alert severity="info" sx={{ mb: 2 }}>
            No tags available. Create tags first before adding them to categories.
          </Alert>
        ) : (
          <DragDropContext onDragEnd={handleOnDragEnd}>
            <Box sx={{ 
              display: 'flex', 
              gap: 2, 
              minHeight: '300px',
              maxHeight: '400px',
              overflow: 'hidden',
              position: 'relative'
            }}>
              {/* Left Column - Available Tags */}
              <Paper 
                elevation={0} 
                variant="outlined" 
                sx={{ 
                  width: '50%', 
                  p: 1,
                  display: 'flex',
                  flexDirection: 'column',
                  flexGrow: 1
                }}
              >
                <Typography variant="subtitle2" sx={{ fontWeight: 'bold', mb: 1 }}>
                  Available Tags
                </Typography>

                <TextField
                  size="small"
                  placeholder="Search tags..."
                  value={searchTerm}
                  onChange={handleSearchChange}
                  variant="outlined"
                  fullWidth
                  sx={{ mb: 1 }}
                  InputProps={{
                    startAdornment: (
                      <InputAdornment position="start">
                        <FaSearch />
                      </InputAdornment>
                    ),
                    endAdornment: searchTerm ? (
                      <InputAdornment position="end">
                        <IconButton 
                          size="small" 
                          onClick={() => setSearchTerm('')}
                          edge="end"
                        >
                          <FaTimes />
                        </IconButton>
                      </InputAdornment>
                    ) : null
                  }}
                />

                {showCreateTag && (
                  <Box sx={{ 
                    display: 'flex', 
                    alignItems: 'center', 
                    mb: 1, 
                    p: 1,
                    bgcolor: 'action.hover',
                    borderRadius: 1
                  }}>
                    <Typography variant="body2" sx={{ flex: 1 }}>
                      Create new tag: <strong>{newTagName}</strong>
                    </Typography>
                    <Button 
                      size="small" 
                      variant="contained" 
                      color="primary" 
                      startIcon={<FaPlus />}
                      onClick={handleCreateNewTag}
                    >
                      Create
                    </Button>
                  </Box>
                )}

                <Droppable droppableId="availableTags">
                  {(provided: DroppableProvided) => (
                    <Box
                      ref={provided.innerRef}
                      {...provided.droppableProps}
                      sx={{ 
                        flex: 1,
                        overflowY: 'auto',
                        minHeight: 0,
                        display: 'flex',
                        flexWrap: 'wrap',
                        alignContent: 'flex-start',
                        gap: 0.5,
                        p: 1
                      }}
                    >
                      {filteredTags
                        .filter(tag => !formData.tags.some(t => t.id === tag._id))
                        .map((tag, index) => (
                          <Draggable key={tag._id} draggableId={tag._id} index={index}>
                            {(provided: DraggableProvided) => (
                              <Chip
                                ref={provided.innerRef}
                                {...provided.draggableProps}
                                {...provided.dragHandleProps}
                                label={tag.name}
                                icon={<FaTag />}
                                onClick={() => handleTagToggle(tag._id, tag.name)}
                                variant="outlined"
                                sx={{ m: 0.5 }}
                              />
                            )}
                          </Draggable>
                        ))}
                      {provided.placeholder}
                      {filteredTags.length === 0 && !showCreateTag && (
                        <Typography variant="body2" color="text.secondary" sx={{ p: 1, width: '100%', textAlign: 'center' }}>
                          No matching tags found
                        </Typography>
                      )}
                    </Box>
                  )}
                </Droppable>
              </Paper>

              {/* Center - Drag Indicator */}
              <Box sx={{ 
                display: 'flex', 
                flexDirection: 'column', 
                justifyContent: 'center',
                alignItems: 'center'
              }}>
                <FaArrowRight />
                <Typography variant="caption" sx={{ mt: 1, textAlign: 'center', width: '60px' }}>
                  Drag and drop
                </Typography>
              </Box>

              {/* Right Column - Selected Tags */}
              <Paper 
                elevation={0} 
                variant="outlined" 
                sx={{ 
                  width: '50%', 
                  p: 1,
                  display: 'flex',
                  flexDirection: 'column',
                  flexGrow: 1
                }}
              >
                <Typography variant="subtitle2" sx={{ fontWeight: 'bold', mb: 1 }}>
                  Selected Tags ({formData.tags.length})
                </Typography>

                <Droppable droppableId="selectedTags">
                  {(provided: DroppableProvided) => (
                    <Box
                      ref={provided.innerRef}
                      {...provided.droppableProps}
                      sx={{ 
                        flex: 1,
                        overflowY: 'auto',
                        minHeight: 0,
                        display: 'flex',
                        flexWrap: 'wrap',
                        alignContent: 'flex-start',
                        gap: 0.5,
                        p: 1,
                        backgroundColor: formData.tags.length ? 'inherit' : 'action.hover'
                      }}
                    >
                      {formData.tags.length > 0 ? (
                        formData.tags.map((tag, index) => (
                          <Draggable key={tag.id} draggableId={tag.id} index={index}>
                            {(provided: DraggableProvided) => (
                              <Chip
                                ref={provided.innerRef}
                                {...provided.draggableProps}
                                {...provided.dragHandleProps}
                                label={tag.name}
                                onDelete={() => handleTagToggle(tag.id, tag.name)}
                                color="primary"
                                size="medium"
                                sx={{ m: 0.5 }}
                              />
                            )}
                          </Draggable>
                        ))
                      ) : (
                        <Typography variant="body2" color="text.secondary" sx={{ p: 1, width: '100%', textAlign: 'center' }}>
                          Drag tags here or click on tags to select
                        </Typography>
                      )}
                      {provided.placeholder}
                    </Box>
                  )}
                </Droppable>
              </Paper>
            </Box>
          </DragDropContext>
        )}
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose}>Cancel</Button>
        <Button 
          onClick={onSubmit} 
          variant="contained" 
          color="primary"
          disabled={!formData.name.trim()}
        >
          {isEditing ? 'Update' : 'Create'}
        </Button>
      </DialogActions>
    </Dialog>
  );
};

// Add default export for lazy loading
export default TagCategoryForm;

import React, { useState, useEffect, useCallback, useMemo, useRef, lazy, Suspense } from 'react';
import {
  Box,
  Typography,
  Button,
  List,
  Paper,
  Alert,
  CircularProgress
} from '@mui/material';
import { FaPlus } from 'react-icons/fa';
import { toast } from 'react-toastify';
import { 
  useTagCategories, 
  useCreateTagCategory,
  useUpdateTagCategory,
  useDeleteTagCategory,
  TagCategory as QueryTagCategory
} from '../../hooks/query-hooks';
import type { NewTagCategoryData } from '../../hooks/query-hooks'; // Import NewTagCategoryData

// Lazy load subcomponents
const TagCategoryForm = lazy(() => import('./TagCategoryForm'));
const TagCategoryItem = lazy(() => import('./TagCategoryItem'));
const DeleteConfirmationDialog = lazy(() => import('./DeleteConfirmationDialog'));

// Match the interface in TagCategoryForm.tsx
interface TagCategoryFormData {
  name: string;
  description: string;
  tags: Array<{ id: string; name: string }>;
}

// Memoized initial form data
const initialFormData: TagCategoryFormData = {
  name: '',
  description: '',
  tags: []
};

// Loading fallback component
const LoadingFallback = () => (
  <Box display="flex" justifyContent="center" alignItems="center" p={2}>
    <CircularProgress size={24} />
  </Box>
);

const TagCategoryManager: React.FC = () => {
  // --- User Profile --- Fetch user profile to pass to hooks
  const { data: userProfile } = useUserProfile(); // Assuming useUserProfile is available and imported

  // Use TanStack Query hooks instead of Redux
  const { 
    data: tagCategories = [],
    isLoading,
    isError,
    error,
    refetch
  } = useTagCategories(userProfile); // Pass userProfile

  const { mutateAsync: createTagCategoryMutation, isPending: isCreatingCategory } = useCreateTagCategory(); // Use isPending
  const { mutateAsync: updateTagCategoryMutation } = useUpdateTagCategory();
  const { mutateAsync: deleteTagCategoryMutation } = useDeleteTagCategory();

  // State management with useRef for values that don't affect rendering
  const operationInProgressRef = useRef<boolean>(false);
  const isMountedRef = useRef<boolean>(true);
  const toastShownRef = useRef<string[]>([]); // Changed from Set to array

  // Local state management
  const [dialogState, setDialogState] = useState({
    open: false,
    deleteDialogOpen: false,
    deleteTarget: null as string | null,
    hardDelete: false,
    editingCategory: null as QueryTagCategory | null,
    creationAttempted: false
  });

  // Use a separate state for form data to prevent re-renders of the entire component
  const [formData, setFormData] = useState<TagCategoryFormData>(initialFormData);

  // Add a flag to disable component-level toasts
  const [disableComponentToasts] = useState(true);

  // Function to show toast only if not already shown for this operation
  const showToastOnce = useCallback((id: string, type: 'success' | 'error', message: string) => {
    // Only show component toast if not disabled
    if (!toastShownRef.current.includes(id)) { // Changed from Set.has to array includes
      // Use standard toast API
      toast[type](message);

      // Mark this toast as shown
      toastShownRef.current.push(id); // Changed from Set.add to array push

      // Clean up old toast IDs periodically (every 100 operations)
      if (toastShownRef.current.length > 100) {
        toastShownRef.current = [id];
      }
    }
  }, []);

  // Component cleanup on unmount
  useEffect(() => {
    return () => {
      isMountedRef.current = false;
    };
  }, []);

  // Dialog handlers with optimized state updates
  const handleOpen = useCallback((category: QueryTagCategory | null = null) => {
    setDialogState(prev => ({
      ...prev,
      open: true,
      editingCategory: category
    }));

    if (category) {
      setFormData({
        name: category.name || '',
        description: category.description || '',
        tags: category.tags?.map(tag => ({
          id: (tag as any)._id || (tag as any).id || '',
          name: tag.name
        })) || []
      });
    } else {
      setFormData(initialFormData);
    }
  }, []);

  const handleClose = useCallback(() => {
    setDialogState(prev => ({
      ...prev,
      open: false
    }));
  }, []);

  const handleDeleteClick = useCallback((id: string) => {
    setDialogState(prev => ({
      ...prev,
      deleteDialogOpen: true,
      deleteTarget: id,
      hardDelete: false
    }));
  }, []);

  const handleCancelDelete = useCallback(() => {
    setDialogState(prev => ({
      ...prev,
      deleteDialogOpen: false
    }));
  }, []);

  const handleHardDeleteChange = useCallback((checked: boolean) => {
    setDialogState(prev => ({
      ...prev,
      hardDelete: checked
    }));
  }, []);

  const handleConfirmDelete = useCallback(async () => {
    const { deleteTarget } = dialogState;
    if (!deleteTarget || operationInProgressRef.current) return;

    operationInProgressRef.current = true;
    toast.info("Tag category deletion is temporarily disabled.");

    // Simulate operation and cleanup
    setTimeout(() => {
      if (isMountedRef.current) {
        setDialogState(prev => ({
          ...prev,
          deleteDialogOpen: false,
          deleteTarget: null,
          hardDelete: false
        }));
        refetch(); // Refetch categories after simulated deletion
        operationInProgressRef.current = false;
      }
    }, 500); 

  }, [dialogState, refetch, showToastOnce]); // Removed tagCategories and deleteTagCategoryMutation

  // Check if category name exists
  const categoryNameExists = useCallback((name: string): boolean => {
    return tagCategories.some(cat => 
      cat.name.toLowerCase() === name.toLowerCase()
    );
  }, [tagCategories]);

  // Handle form submission
  const handleSubmit = useCallback(async (submittedFormData: TagCategoryFormData) => {
    if (!submittedFormData.name.trim()) {
      const errorId = `empty-name-error-${Date.now()}`;
      showToastOnce(errorId, 'error', 'Category name is required');
      return;
    }

    if (operationInProgressRef.current || isCreatingCategory /* || isUpdatingCategory */) return; // Add loading checks

    // operationInProgressRef.current = true; // Mutation hook handles its own loading state
    // toast.info("Tag category creation/update is temporarily disabled."); // Remove this

    const { editingCategory } = dialogState;

    try {
      operationInProgressRef.current = true; // Still useful to prevent double-submit before mutation state updates

      // Check for duplicate name
      const exists = categoryNameExists(submittedFormData.name);
      const isEditingSameName = editingCategory && 
        editingCategory.name.toLowerCase() === submittedFormData.name.toLowerCase();

      if (exists && (!editingCategory || !isEditingSameName)) {
        const errorId = `duplicate-name-${submittedFormData.name}-${Date.now()}`;
        showToastOnce(errorId, 'error', `Category "${submittedFormData.name}" already exists. Please choose a different name.`);
        operationInProgressRef.current = false;
        return;
      }

      const categoryData: NewTagCategoryData = {
        name: submittedFormData.name,
        description: submittedFormData.description,
        // Ensure tags are in the format { id: string, name: string } if your API expects that for creation
        // Or adjust if it only needs an array of tag names or IDs.
        // For now, assuming it matches TagCategoryFormData's structure.
        tags: submittedFormData.tags.map(tag => ({ id: tag.id, name: tag.name })) 
      };

      if (editingCategory) {
        // TODO: Implement update logic using updateTagCategoryMutation
        toast.info('Update functionality for tag categories is not yet implemented.');
        operationInProgressRef.current = false; // Reset for now
      } else {
        // Create using TanStack Query mutation
        await createTagCategoryMutation(categoryData, {
          onSuccess: () => {
            if (isMountedRef.current) {
              handleClose();
              // refetch(); // Invalidation in useCreateTagCategory handles this
            }
          },
          // onError is handled by the useCreateTagCategory hook's toast
        });
      }
    } catch (error: any) {
      // This catch block might be redundant if the hook handles all errors with toasts
      // However, keeping it for now for any local errors or direct throws
      console.error('Error submitting tag category:', error);
      const errorId = `submit-error-${Date.now()}`;
      showToastOnce(errorId, 'error', `Failed to save category: ${error.message || 'Unknown error'}`);
    } finally {
      if (isMountedRef.current) {
        // Only set to false if not handled by mutation states, or if you want to allow re-submission after a local error
        // For now, let the mutation hook's pending state manage this more globally.
        // operationInProgressRef.current = false;
      }
    }
  }, [dialogState, showToastOnce, categoryNameExists, handleClose, refetch, createTagCategoryMutation, isCreatingCategory]); 

  // Memoized derived state
  const isEmpty = useMemo(() => 
    tagCategories.length === 0, 
    [tagCategories.length]
  );

  const categoryNameToDelete = useMemo(() => 
    dialogState.deleteTarget ? 
      tagCategories.find(c => c._id === dialogState.deleteTarget)?.name || '' : 
      '', 
    [dialogState.deleteTarget, tagCategories]
  );

  // Render loading state
  if (isLoading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" padding={4}>
        <CircularProgress />
      </Box>
    );
  }

  // Render error state
  if (isError) {
    return (
      <Box sx={{ mt: 2, mb: 2 }}>
        <Alert severity="error" sx={{ mb: 2 }}>
          {error instanceof Error ? error.message : 'Failed to load tag categories'}
        </Alert>
        <Button variant="outlined" onClick={() => refetch()}>
          Try Again
        </Button>
      </Box>
    );
  }

  return (
    <Box className="tag-category-manager" sx={{ mt: 4 }}>
      {/* Header with action buttons */}
      <Box display="flex" justifyContent="space-between" alignItems="center" mb={2}>
        <Box>
          <Typography variant="h5">Tag Categories</Typography>
        </Box>
        <Button
          variant="contained"
          color="primary"
          startIcon={<FaPlus />}
          onClick={() => handleOpen()}
          sx={{
            minWidth: '160px',
            height: '38px'
          }}
        >
          Create Category
        </Button>
      </Box>

      {/* Warning for creation attempts with no visible results */}
      {isEmpty && dialogState.creationAttempted && (
        <Alert severity="warning" sx={{ mb: 2 }}>
          Category was created but isn't displaying. Try refreshing the page.
        </Alert>
      )}

      {/* Display tag categories or empty state */}
      {isEmpty ? (
        <Alert severity="info">No tag categories found. Create one to get started.</Alert>
      ) : (
        <Paper elevation={2}>
          <List>
            {tagCategories.map((category, index) => (
              <Suspense key={category._id} fallback={<LoadingFallback />}>
                <TagCategoryItem 
                  category={category}
                  index={index}
                  totalCount={tagCategories.length}
                  onEdit={() => handleOpen(category)}
                  onDelete={() => handleDeleteClick(category._id)}
                />
              </Suspense>
            ))}
          </List>
        </Paper>
      )}

      {/* Conditionally render dialogs only when needed */}
      {dialogState.open && (
        <Suspense fallback={<LoadingFallback />}>
          <TagCategoryForm
            open={dialogState.open}
            formData={formData}
            setFormData={setFormData}
            onClose={handleClose}
            onSubmit={() => handleSubmit(formData)}
            isEditing={!!dialogState.editingCategory}
          />
        </Suspense>
      )}

      {dialogState.deleteDialogOpen && (
        <Suspense fallback={<LoadingFallback />}>
          <DeleteConfirmationDialog
            open={dialogState.deleteDialogOpen}
            onClose={handleCancelDelete}
            onConfirm={handleConfirmDelete}
            hardDelete={dialogState.hardDelete}
            setHardDelete={handleHardDeleteChange}
            categoryName={categoryNameToDelete}
          />
        </Suspense>
      )}
    </Box>
  );
};

export default React.memo(TagCategoryManager);

import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Typography, 
  TextField, 
  Button, 
  FormControl,
  FormHelperText,
  InputLabel, 
  MenuItem, 
  Select,
  Paper,
  CircularProgress,
  Collapse,
  Alert,
  IconButton
} from '@mui/material';
import { Close as CloseIcon } from '@mui/icons-material';
import { useUserProfile, useSendInvitation, InvitationData } from '../../hooks/query-hooks';

interface InvitationFormProps {
  onInvitationSent?: () => void;
}

const InvitationForm: React.FC<InvitationFormProps> = ({ onInvitationSent }) => {
  const { data: userProfile } = useUserProfile();
  const { mutate: sendInvitationMutate, isPending: isLoadingInvitation, error: invitationErrorHook } = useSendInvitation();

  const [formData, setFormData] = useState({
    email: '',
    firstName: '',
    lastName: '',
    role: 'user',
    message: ''
  });

  const [error, setError] = useState<string | null>(null);
  const [success, setSuccess] = useState<string | null>(null);

  useEffect(() => {
    if (invitationErrorHook) {
      const message =
        (invitationErrorHook as any).response?.data?.message ||
        (invitationErrorHook as any).message ||
        'Failed to send invitation. Please try again.';
      setError(message);
    } else {
      setError(null);
    }
  }, [invitationErrorHook]);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement> | any) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const validateEmail = (email: string): boolean => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const resetMessages = () => {
    setError(null);
    setSuccess(null);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    resetMessages();

    if (!formData.email || !formData.firstName || !formData.lastName) {
      setError('Email, first name, and last name are required');
      return;
    }

    if (!validateEmail(formData.email)) {
      setError('Please enter a valid email address');
      return;
    }

    if (!userProfile?._id) {
      setError('Could not identify the current user. Please try logging in again.');
      return;
    }

    const invitationPayload: InvitationData = {
      ...formData,
      role: formData.role as InvitationData['role'],
      invitedBy: userProfile._id
    };

    sendInvitationMutate(invitationPayload, {
      onSuccess: (data) => {
        setSuccess(data.message || `Invitation sent to ${formData.email} successfully!`);
        setFormData({
          email: '',
          firstName: '',
          lastName: '',
          role: 'user',
          message: ''
        });
        if (onInvitationSent) {
          onInvitationSent();
        }
      },
      onError: (error: any) => {
        console.error('Error sending invitation from component callback:', error);
      }
    });
  };

  return (
    <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
      <Typography variant="h5" gutterBottom>
        Invite New User
      </Typography>

      <Typography variant="body2" color="text.secondary" paragraph>
        Send an email invitation to a new user to join Omni's Media Library.
      </Typography>

      <Collapse in={!!success}>
        <Alert 
          severity="success"
          action={
            <IconButton
              aria-label="close"
              color="inherit"
              size="small"
              onClick={() => setSuccess(null)}
            >
              <CloseIcon fontSize="inherit" />
            </IconButton>
          }
          sx={{ mb: 2 }}
        >
          {success}
        </Alert>
      </Collapse>

      <Collapse in={!!error}>
        <Alert 
          severity="error"
          action={
            <IconButton
              aria-label="close"
              color="inherit"
              size="small"
              onClick={() => setError(null)}
            >
              <CloseIcon fontSize="inherit" />
            </IconButton>
          }
          sx={{ mb: 2 }}
        >
          {error}
        </Alert>
      </Collapse>

      <Box component="form" onSubmit={handleSubmit} noValidate>
        <Box sx={{ display: 'flex', gap: 2, mb: 2 }}>
          <TextField
            name="firstName"
            label="First Name"
            value={formData.firstName}
            onChange={handleChange}
            fullWidth
            required
            autoFocus
            autoComplete="given-name"
            disabled={isLoadingInvitation}
          />

          <TextField
            name="lastName"
            label="Last Name"
            value={formData.lastName}
            onChange={handleChange}
            fullWidth
            required
            autoComplete="family-name"
            disabled={isLoadingInvitation}
          />
        </Box>
        <Box sx={{ display: 'flex', gap: 2, mb: 2 }}>
        <TextField
          name="email"
          label="Email Address"
          value={formData.email}
          onChange={handleChange}
          fullWidth
          required
          margin="normal"
          type="email"
          autoComplete="email"
          disabled={isLoadingInvitation}
        />

        <FormControl fullWidth margin="normal" disabled={isLoadingInvitation}>
          <InputLabel id="role-label">Role</InputLabel>
          <Select
            labelId="role-label"
            name="role"
            value={formData.role}
            onChange={handleChange}
            label="Role"
            disabled={isLoadingInvitation}
          >
            <MenuItem value="user">User</MenuItem>
            <MenuItem value="distributor">Distributor</MenuItem>
            <MenuItem value="admin">Admin</MenuItem>
          </Select>
          <FormHelperText>
            Select the role for the new user
          </FormHelperText>
        </FormControl>
        </Box>

        <TextField
          name="message"
          label="Personal Message (Optional)"
          value={formData.message}
          onChange={handleChange}
          fullWidth
          margin="normal"
          multiline
          rows={3}
          placeholder="Add a personal message to include in the invitation email"
          disabled={isLoadingInvitation}
        />

        <Box sx={{ mt: 3, display: 'flex', justifyContent: 'flex-end' }}>
          <Button
            type="submit"
            variant="contained"
            color="primary"
            sx={{ minWidth: 120 }}
            disabled={isLoadingInvitation}
          >
            {isLoadingInvitation ? <CircularProgress size={24} sx={{ color: 'white'}} /> : 'Send Invitation'}
          </Button>
        </Box>
      </Box>
    </Paper>
  );
};

export default InvitationForm;

import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  Chip,
  IconButton,
  Button,
  Tooltip,
  CircularProgress,
  Alert,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogContentText,
  DialogActions,
  Container
} from '@mui/material';
import {
  Refresh as RefreshIcon,
  Send as ResendIcon,
  Delete as DeleteIcon,
  DeleteForever as DeleteForeverIcon
} from '@mui/icons-material';
import axios from 'axios';
import { API_BASE_URL } from '../../config/config';
import { formatDistance } from 'date-fns';
import './invitationList.scss'; // Import the new SCSS file

interface Invitation {
  _id: string;
  email: string;
  firstName: string;
  lastName: string;
  role: string;
  status: 'pending' | 'accepted' | 'expired' | 'cancelled';
  expiresAt: string;
  createdAt: string;
  invitedBy: {
    _id: string;
    firstName: string;
    lastName: string;
    email: string;
  };
}

interface InvitationListProps {
  refreshTrigger?: number;
  onRefresh?: () => void;
}

const InvitationList: React.FC<InvitationListProps> = ({ refreshTrigger, onRefresh }) => {
  // State
  const [invitations, setInvitations] = useState<Invitation[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);
  const [page, _setPage] = useState<number>(0);
  const [rowsPerPage, _setRowsPerPage] = useState<number>(10);
  const [invitationToCancel, setInvitationToCancel] = useState<Invitation | null>(null);
  const [invitationToResend, setInvitationToResend] = useState<Invitation | null>(null);
  const [actionLoading, setActionLoading] = useState<string | null>(null);
  const [actionSuccess, setActionSuccess] = useState<string | null>(null);
  const [invitationToDeletePermanently, setInvitationToDeletePermanently] = useState<Invitation | null>(null);
  const [deletePermanentlyDialogOpen, setDeletePermanentlyDialogOpen] = useState<boolean>(false);

  // Load invitations
  const fetchInvitations = async () => {
    try {
      setLoading(true);
      setError(null);

      // Get token
      const token = localStorage.getItem('authToken');

      if (!token) {
        setError('You must be logged in to view invitations');
        setLoading(false);
        return;
      }

      // Fetch invitations
      const response = await axios.get<Invitation[]>(`${API_BASE_URL}/api/invitations`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });

      setInvitations(response.data);

    } catch (err: any) {
      console.error('Error fetching invitations:', err);

      if (err.response?.data?.message) {
        setError(err.response.data.message);
      } else {
        setError('An error occurred while fetching invitations');
      }
    } finally {
      setLoading(false);
    }
  };

  // Load on mount and when refreshTrigger changes
  useEffect(() => {
    fetchInvitations();
  }, [refreshTrigger]);

  // Cancel invitation
  const handleCancelInvitation = async () => {
    if (!invitationToCancel) return;

    try {
      setActionLoading(`cancel-${invitationToCancel._id}`);

      // Get token
      const token = localStorage.getItem('authToken');

      if (!token) {
        setError('You must be logged in to cancel invitations');
        setActionLoading(null);
        return;
      }

      // Cancel invitation
      await axios.delete(`${API_BASE_URL}/api/invitations/${invitationToCancel._id}`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });

      // Update local state
      setInvitations(prev => 
        prev.map(inv => 
          inv._id === invitationToCancel._id 
            ? { ...inv, status: 'cancelled' } 
            : inv
        )
      );

      setActionSuccess(`Invitation to ${invitationToCancel.email} has been cancelled`);
      setInvitationToCancel(null);

    } catch (err: any) {
      console.error('Error cancelling invitation:', err);

      if (err.response?.data?.message) {
        setError(err.response.data.message);
      } else {
        setError('An error occurred while cancelling the invitation');
      }
    } finally {
      setActionLoading(null);
    }
  };

  // Resend invitation
  const handleResendInvitation = async () => {
    if (!invitationToResend) return;

    try {
      setActionLoading(`resend-${invitationToResend._id}`);

      // Get token
      const token = localStorage.getItem('authToken');

      if (!token) {
        setError('You must be logged in to resend invitations');
        setActionLoading(null);
        return;
      }

      // Resend invitation
      await axios.post(`${API_BASE_URL}/api/invitations/${invitationToResend._id}/resend`, {}, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });

      // Update local state to refresh expiration date
      fetchInvitations();

      setActionSuccess(`Invitation has been resent to ${invitationToResend.email}`);
      setInvitationToResend(null);

    } catch (err: any) {
      console.error('Error resending invitation:', err);

      if (err.response?.data?.message) {
        setError(err.response.data.message);
      } else {
        setError('An error occurred while resending the invitation');
      }
    } finally {
      setActionLoading(null);
    }
  };

  // --- New: Delete Permanently ---
  const handleDeletePermanentlyClicked = (invitation: Invitation) => {
    setInvitationToDeletePermanently(invitation);
    setDeletePermanentlyDialogOpen(true);
  };

  const handleDeletePermanentlySubmit = async () => {
    if (!invitationToDeletePermanently) return;

    try {
      setActionLoading(`delete-perm-${invitationToDeletePermanently._id}`);
      const token = localStorage.getItem('authToken');
      if (!token) {
        setError('You must be logged in to delete invitations');
        setActionLoading(null);
        return;
      }

      await axios.delete(`${API_BASE_URL}/api/invitations/${invitationToDeletePermanently._id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setInvitations(prev => prev.filter(inv => inv._id !== invitationToDeletePermanently._id));
      setActionSuccess(`Invitation to ${invitationToDeletePermanently.email} has been permanently deleted.`);
      setInvitationToDeletePermanently(null);
      setDeletePermanentlyDialogOpen(false);

    } catch (err: any) {
      console.error('Error permanently deleting invitation:', err);
      setError(err.response?.data?.message || 'An error occurred while permanently deleting the invitation.');
    } finally {
      setActionLoading(null);
    }
  };

  // Handle refresh
  const handleRefresh = () => {
    fetchInvitations();
    if (onRefresh) onRefresh();
  };


  // Render status chip
  const renderStatusChip = (status: string) => {
    let color: 'default' | 'primary' | 'secondary' | 'error' | 'info' | 'success' | 'warning' = 'default';

    switch (status) {
      case 'pending':
        color = 'info';
        break;
      case 'accepted':
        color = 'success';
        break;
      case 'expired':
        color = 'warning';
        break;
      case 'cancelled':
        color = 'error';
        break;
    }

    return (
      <Chip 
        label={status} 
        color={color} 
        size="small"
        sx={{ textTransform: 'capitalize' }}
      />
    );
  };

  // Check if invitation is expired
  const isExpired = (expiresAt: string) => {
    return new Date(expiresAt) < new Date();
  };

  // Format date
  const formatDate = (dateString: string) => {
    const date = new Date(dateString);

    // For expiration dates, show relative time
    try {
      return formatDistance(date, new Date(), { addSuffix: true });
    } catch (error) {
      return dateString;
    }
  };

  return (
    <Container className="invitation-list-container">
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
        <Typography variant="h5">
          Invitations
        </Typography>

        <Button 
          startIcon={<RefreshIcon />} 
          onClick={handleRefresh}
          disabled={loading}
        >
          Refresh
        </Button>
      </Box>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}

      {actionSuccess && (
        <Alert 
          severity="success" 
          sx={{ mb: 2 }}
          onClose={() => setActionSuccess(null)}
        >
          {actionSuccess}
        </Alert>
      )}

      {loading ? (
        <Box sx={{ display: 'flex', justifyContent: 'center', p: 3 }}>
          <CircularProgress />
        </Box>
      ) : invitations.length === 0 ? (
        <Alert severity="info">
          No invitations found. Use the form above to invite new users.
        </Alert>
      ) : (
        <>
          <div className="invitation-custom-list">
            {/* Header Row */}
            <div className="invitation-list-header">
              <div className="invitation-header-cell">Name</div>
              <div className="invitation-header-cell">Email</div>
              <div className="invitation-header-cell">Role</div>
              <div className="invitation-header-cell">Status</div>
              <div className="invitation-header-cell">Expires</div>
              <div className="invitation-header-cell">Created</div>
              <div className="invitation-header-cell actions-header">Actions</div>
            </div>

            {/* Data Rows */}
            {invitations
              .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage) // Pagination logic still applied here
              .map((invitation, index) => (
                <div 
                  className={`invitation-list-row ${index % 2 === 0 ? 'invitation-list-row--even' : 'invitation-list-row--odd'}`} 
                  key={invitation._id}
                >
                  <div className="invitation-data-cell" data-label="Name">
                    {invitation.firstName} {invitation.lastName}
                  </div>
                  <div className="invitation-data-cell" data-label="Email">{invitation.email}</div>
                  <div className="invitation-data-cell" data-label="Role" style={{ textTransform: 'capitalize' }}>
                    {invitation.role}
                  </div>
                  <div className="invitation-data-cell" data-label="Status">
                    {invitation.status === 'pending' && isExpired(invitation.expiresAt)
                      ? renderStatusChip('expired')
                      : renderStatusChip(invitation.status)}
                  </div>
                  <div className="invitation-data-cell" data-label="Expires">
                    {formatDate(invitation.expiresAt)}
                  </div>
                  <div className="invitation-data-cell" data-label="Created">
                    {formatDate(invitation.createdAt)}
                  </div>
                  <div className="invitation-data-cell invitation-actions-cell" data-label="Actions">
                    {invitation.status === 'pending' && !isExpired(invitation.expiresAt) && (
                      <>
                        <Tooltip title="Cancel Invitation">
                          <IconButton
                            size="small"
                            onClick={() => setInvitationToCancel(invitation)}
                            disabled={!!actionLoading}
                            color="warning"
                            className="action-button cancel-button"
                          >
                            <DeleteIcon fontSize="small" />
                          </IconButton>
                        </Tooltip>
                        <Tooltip title="Resend Invitation">
                          <IconButton
                            size="small"
                            onClick={() => setInvitationToResend(invitation)}
                            disabled={!!actionLoading}
                            color="primary"
                            className="action-button resend-button"
                          >
                            <ResendIcon fontSize="small" />
                          </IconButton>
                        </Tooltip>
                      </>
                    )}
                    {invitation.status === 'cancelled' && (
                      <Tooltip title="Delete Permanently">
                        <IconButton
                          size="small"
                          onClick={() => handleDeletePermanentlyClicked(invitation)}
                          disabled={!!actionLoading}
                          color="error"
                          className="action-button delete-permanently-button"
                        >
                          <DeleteForeverIcon fontSize="small" />
                        </IconButton>
                      </Tooltip>
                    )}
                    {/* Placeholder for other statuses if needed */}
                  </div>
                </div>
              ))}
          </div>

          {/* 
          <TablePagination
            rowsPerPageOptions={[5, 10, 25]}
            component="div"
            count={invitations.length}
            rowsPerPage={rowsPerPage}
            page={page}
            onPageChange={handleChangePage}
            onRowsPerPageChange={handleChangeRowsPerPage}
          /> 
          */}
        </>
      )}

      {/* Cancel Confirmation Dialog */}
      <Dialog
        open={!!invitationToCancel}
        onClose={() => setInvitationToCancel(null)}
      >
        <DialogTitle>Cancel Invitation</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Are you sure you want to cancel the invitation sent to{' '}
            <strong>{invitationToCancel?.email}</strong>?
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setInvitationToCancel(null)}>No, Keep It</Button>
          <Button onClick={handleCancelInvitation} color="error" autoFocus>
            Yes, Cancel Invitation
          </Button>
        </DialogActions>
      </Dialog>

      {/* Resend Confirmation Dialog */}
      <Dialog
        open={!!invitationToResend}
        onClose={() => setInvitationToResend(null)}
      >
        <DialogTitle>Resend Invitation</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Do you want to resend the invitation to{' '}
            <strong>{invitationToResend?.email}</strong>?
          </DialogContentText>
          <DialogContentText sx={{ mt: 2 }}>
            This will send a new email and extend the expiration date.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setInvitationToResend(null)}>Cancel</Button>
          <Button onClick={handleResendInvitation} color="primary" autoFocus>
            Resend Invitation
          </Button>
        </DialogActions>
      </Dialog>

      {/* --- New: Confirmation Dialog for Permanent Deletion --- */}
      <Dialog open={deletePermanentlyDialogOpen} onClose={() => setDeletePermanentlyDialogOpen(false)}>
        <DialogTitle>Confirm Permanent Delete</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Are you sure you want to permanently delete the invitation for {' '}
            <strong>{invitationToDeletePermanently?.email}</strong>? 
            This action cannot be undone.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setDeletePermanentlyDialogOpen(false)} color="inherit" disabled={!!actionLoading}>
            Cancel
          </Button>
          <Button 
            onClick={handleDeletePermanentlySubmit} 
            color="error" 
            variant="contained"
            disabled={!!actionLoading}
            startIcon={actionLoading === `delete-perm-${invitationToDeletePermanently?._id}` ? <CircularProgress size={16} color="inherit" /> : null}
          >
            {actionLoading === `delete-perm-${invitationToDeletePermanently?._id}` ? 'Deleting...' : 'Delete Permanently'}
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default InvitationList;

import React, { useState, useRef, useEffect, useMemo } from 'react';
import {
  Box,
  Slider,
  IconButton,
  Typography,
  Button,
  CircularProgress,
  Alert,
  Fade
} from '@mui/material';
import {
  PlayArrow,
  Pause,
  PhotoCamera,
  ErrorOutline,
  CheckCircle,
  AccessTime,
  ArrowForward,
  FastForward,
  FastRewind,
  Replay
} from '@mui/icons-material';
import './VideoThumbnailSelector.scss';
import axios from 'axios';
import env from '../../config/env';
import { toast } from 'react-toastify';

interface MediaDetailThumbnailSelectorProps {
  videoUrl: string;
  mediaId: string;
  currentThumbnail?: string;
  onThumbnailUpdate: (thumbnailUrl: string) => void;
  mediaData?: any; // Optional complete media data from parent
  onClose?: () => void; // Optional close handler for dialog mode
}

interface ThumbnailResponse {
  success?: boolean;
  message?: string;
  timestamp?: string;
  thumbnailUrl?: string;
  mediaFile?: any;
}

const MediaDetailThumbnailSelector: React.FC<MediaDetailThumbnailSelectorProps> = ({
  videoUrl,
  mediaId,
  currentThumbnail,
  onThumbnailUpdate,
}) => {
  const videoRef = useRef<HTMLVideoElement>(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const [isSliding, setIsSliding] = useState(false);
  const [seekTime, setSeekTime] = useState<number | null>(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [refreshKey, setRefreshKey] = useState<string | number>(0);
  const [isVideoLoading, setIsVideoLoading] = useState(true);
  const [videoError, setVideoError] = useState<string | null>(null);
  const [thumbnailUpdated, setThumbnailUpdated] = useState(false);
  const [showSuccessIndicator, setShowSuccessIndicator] = useState(false);
  const [processingProgress, setProcessingProgress] = useState(0);
  const [oldThumbnail, setOldThumbnail] = useState<string | null>(null);
  const [showOverlay, setShowOverlay] = useState(false);

  useEffect(() => {
    const video = videoRef.current;
    if (!video) return;

    video.src = videoUrl;
    setIsVideoLoading(true);
    setVideoError(null);

    const handleLoadedMetadata = () => {
      setDuration(video.duration);
      setIsVideoLoading(false);
      video.currentTime = 0;
    };

    const handleTimeUpdate = () => {
      if (!isSliding) {
        setCurrentTime(video.currentTime);
      }
    };

    const handleSeeked = () => {
      setCurrentTime(video.currentTime);
      video.pause();
      setIsPlaying(false);
    };

    const handleError = (_e: Event) => {
      setVideoError('Failed to load video. Please check the URL and your connection.');
      setIsVideoLoading(false);
    };

    const handleLoadStart = () => {
      setIsVideoLoading(true);
    };

    video.addEventListener('loadedmetadata', handleLoadedMetadata);
    video.addEventListener('timeupdate', handleTimeUpdate);
    video.addEventListener('seeked', handleSeeked);
    video.addEventListener('error', handleError);
    video.addEventListener('loadstart', handleLoadStart);

    video.load();

    return () => {
      video.removeEventListener('loadedmetadata', handleLoadedMetadata);
      video.removeEventListener('timeupdate', handleTimeUpdate);
      video.removeEventListener('seeked', handleSeeked);
      video.removeEventListener('error', handleError);
      video.removeEventListener('loadstart', handleLoadStart);
      video.src = '';
    };
  }, [videoUrl]);

  useEffect(() => {
    if (seekTime !== null && videoRef.current) {
      videoRef.current.currentTime = seekTime;
    }
  }, [seekTime]);

  const togglePlay = () => {
    if (videoRef.current) {
      if (isPlaying) {
        videoRef.current.pause();
      } else {
        try {
          const playPromise = videoRef.current.play();
          if (playPromise !== undefined) {
            playPromise.catch(error => {
              setVideoError(`Error playing video: ${error.message}`);
            });
          }
        } catch (error) {
          setVideoError(`Error playing video: ${error instanceof Error ? error.message : String(error)}`);
        }
      }
      setIsPlaying(!isPlaying);
    }
  };

  const handleSliderChange = (_event: Event, newValue: number | number[]) => {
    const time = newValue as number;
    setCurrentTime(time);
    setSeekTime(time);
  };

  const handleSliderDragStart = () => {
    setIsSliding(true);
    if (isPlaying) {
      videoRef.current?.pause();
      setIsPlaying(false);
    }
  };

  const handleSliderDragEnd = () => {
    setIsSliding(false);
    if (videoRef.current) {
      videoRef.current.currentTime = currentTime;
    }
  };

  const formatTime = (seconds: number) => {
    const h = Math.floor(seconds / 3600);
    const m = Math.floor((seconds % 3600) / 60);
    const s = Math.floor(seconds % 60);
    return `${h.toString().padStart(2, '0')}:${m.toString().padStart(2, '0')}:${s.toString().padStart(2, '0')}`;
  };

  const handleStepForward = () => {
    if (videoRef.current) {
      const newTime = Math.min(currentTime + 1, duration);
      videoRef.current.currentTime = newTime;
      setCurrentTime(newTime);
    }
  };

  const handleStepBackward = () => {
    if (videoRef.current) {
      const newTime = Math.max(currentTime - 1, 0);
      videoRef.current.currentTime = newTime;
      setCurrentTime(newTime);
    }
  };

  const resetToStart = () => {
    if (videoRef.current) {
      videoRef.current.currentTime = 0;
      setCurrentTime(0);
      videoRef.current.pause();
      setIsPlaying(false);
    }
  };

  const simulateProgress = () => {
    let progress = 0;
    const interval = setInterval(() => {
      progress += Math.random() * 15;
      if (progress > 100) {
        progress = 100;
        clearInterval(interval);
      }
      setProcessingProgress(progress);
    }, 200);

    return () => clearInterval(interval);
  };

  const sendTimestampToServer = async (timestamp: string) => {
    try {
      // Save the old thumbnail URL to potentially show a comparison
      if (currentThumbnail) {
        setOldThumbnail(currentThumbnail);
      }

      setIsProcessing(true);
      setThumbnailUpdated(false);
      setShowOverlay(false);

      // Start progress simulation
      const stopProgressSimulation = simulateProgress();

      const response = await axios.post<ThumbnailResponse>(
        `${env.BASE_URL}/api/media/update/timestamp-thumbnail/${mediaId}`,
        { timestamp },
        { 
          withCredentials: true,
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${localStorage.getItem('authToken')}`
          }
        }
      );

      // Stop progress simulation
      stopProgressSimulation();
      setProcessingProgress(100);

      if (response.data.success && response.data.thumbnailUrl) {
        const originalS3Url = response.data.thumbnailUrl;

        const timeValue = Date.now();
        const uniqueId = `${timeValue}`;

        setRefreshKey(uniqueId);
        setThumbnailUpdated(true);

        // Notify parent component about the update
        onThumbnailUpdate(originalS3Url);

        // Show success indicator
        setShowSuccessIndicator(true);
        setTimeout(() => setShowSuccessIndicator(false), 3000);

        // Reset video to beginning and show overlay
        resetToStart();
        setTimeout(() => {
          setShowOverlay(true);
        }, 300);

        // Toast notification is now handled by parent component
      } else {
        toast.error('Failed to update thumbnail: No URL returned');
      }
    } catch (error) {
      toast.error('Error updating thumbnail');
    } finally {
      setIsProcessing(false);
    }
  };

  const handleCaptureThumbnail = () => {
    const timestamp = formatTime(currentTime);
    sendTimestampToServer(timestamp);
  };

  const handleHideOverlay = () => {
    setShowOverlay(false);
  };

  const displayThumbnailUrl = useMemo(() => {
    if (!currentThumbnail) return undefined;

    // If already a proxied URL, just use it directly
    if (currentThumbnail.includes('/thumbnail-proxy/')) {
      return currentThumbnail;
    }

    // Extract base URL without query parameters
    const baseUrl = currentThumbnail.split('?')[0];

    // Create a timestamp that changes only when refreshKey changes
    // This prevents multiple requests during renders
    const uniqueId = `${refreshKey}`;

    return `${baseUrl}?nocache=${uniqueId}`;
  }, [currentThumbnail, refreshKey]);

  const retryVideoLoad = () => {
    if (videoRef.current) {
      setVideoError(null);
      setIsVideoLoading(true);
      videoRef.current.load();
    }
  };

  return (
    <Box
      className="video-thumbnail-selector media-detail-mode"
      key={refreshKey}
    >
      <Box className="video-container">
        {videoError && (
          <Box className="video-error">
            <Alert
              severity="error"
              icon={<ErrorOutline />}
              action={
                <Button color="inherit" size="small" onClick={retryVideoLoad}>
                  Retry
                </Button>
              }
            >
              {videoError}
            </Alert>
          </Box>
        )}

        {isVideoLoading && !videoError && (
          <Box className="video-loading">
            <CircularProgress size={40} />
            <Typography variant="body2" sx={{ mt: 2 }}>
              Loading video...
            </Typography>
          </Box>
        )}

        <video
          ref={videoRef}
          style={{
            width: "100%",
            maxHeight: "300px",
            display: videoError ? "none" : "block",
          }}
          playsInline
          onPlay={() => setIsPlaying(true)}
          onPause={() => setIsPlaying(false)}
        />

        {showOverlay && displayThumbnailUrl && (
          <Box 
            className="thumbnail-overlay"
            onClick={handleHideOverlay}
            sx={{
              position: 'absolute',
              top: 0,
              left: 0,
              width: '100%',
              height: '100%',
